(233, '_transient_feed_c8dc84948ab55424f034a4ced348e013', 'a:4:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:52:\"\n	\n	\n	\n	\n	\n	\n	\n	\n	\n\n \n	\n		\n		\n		\n		\n		\n		\n		\n		\n		\n	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:3:{s:0:\"\";a:8:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"OceanWP\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:19:\"https://oceanwp.org\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"Free Multi-Purpose WordPress Theme\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:13:\"lastBuildDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 17 May 2022 07:53:17 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"language\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"en-US\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"generator\";a:1:{i:0;a:5:{s:4:\"data\";s:30:\"https://wordpress.org/?v=5.9.3\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"image\";a:1:{i:0;a:6:{s:4:\"data\";s:11:\"\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:5:{s:3:\"url\";a:1:{i:0;a:5:{s:4:\"data\";s:74:\"https://oceanwp.org/wp-content/uploads/2016/10/cropped-favicon-150x150.png\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"OceanWP\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:19:\"https://oceanwp.org\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"width\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"32\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:6:\"height\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"32\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}s:4:\"item\";a:10:{i:0;a:6:{s:4:\"data\";s:79:\"\n		\n		\n					\n		\n		\n		\n				\n		\n		\n		\n\n					\n										\n					\n					\n			\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"OceanWP Starboard Update: Our Theme Panel Received a Makeover!\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:54:\"https://oceanwp.org/blog/oceanwp-theme-panel-makeover/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"https://oceanwp.org/blog/oceanwp-theme-panel-makeover/#respond\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 17 May 2022 07:35:43 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:3:{i:0;a:5:{s:4:\"data\";s:6:\"Update\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:11:\"Introducing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:9:\"WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://oceanwp.org/?p=633617\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1245:\"<p style=\"display: block; padding: 0 0 24px; margin: 0 !important;\"><a href=\"https://oceanwp.org/blog/oceanwp-theme-panel-makeover/\" target=\"_blank\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/New-Project-6.png\" width=\"1200\" height=\"700\" title=\"OceanWP Starboard Update: Our Theme Panel Received a Makeover!\" alt=\"OceanWP Starboard Update: Our Theme Panel Received a Makeover!\" style=\"width: 100%!important;\"></a></p><p style=\"padding: 0 26px !important; margin: 0 !important;\">Since the Ocean Breeze update, which marked the Spring here at OceanWP, we have been extremely busy working on new improvements for you. Really no use keeping it a secret &#8211; our theme panel has received a makeover! The upcoming OceanWP update brings you the new OceanWP Panel, previously known as the Theme Panel that [&#8230;]<p style=\"margin: 0!important;padding: 26px 0 0!important;\"><a href=\"https://oceanwp.org/blog/oceanwp-theme-panel-makeover/\" target=\"_blank\" style=\"display: inline-block; padding: 10px 30px; color: #ffffff; background-color: #13aff0; border-radius: 30px; font-size: 13px; font-weight: bold; line-height: 1.4; text-transform: uppercase; text-align: center; text-decoration: none; letter-spacing: 1px;\">Read More</a></p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Lior Zelering\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:53399:\"<p style=\"display: block; padding: 0 0 24px; margin: 0 !important;\"><a href=\"https://oceanwp.org/blog/oceanwp-theme-panel-makeover/\" target=\"_blank\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/New-Project-6.png\" width=\"1200\" height=\"700\" title=\"OceanWP Starboard Update: Our Theme Panel Received a Makeover!\" alt=\"OceanWP Starboard Update: Our Theme Panel Received a Makeover!\" style=\"width: 100%!important;\"></a></p><p style=\"padding: 0 26px !important; margin: 0 !important;\">\n<p class=\"has-medium-font-size\">Since the <a href=\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/\" data-type=\"post\" data-id=\"631456\">Ocean Breeze update</a>, which marked the Spring here at OceanWP, we have been extremely busy working on new improvements for you. Really no use keeping it a secret &#8211; our theme panel has received a makeover!</p>\n\n\n\n<p>The <strong>upcoming OceanWP update brings you the new OceanWP Panel, previously known as the Theme Panel</strong> that we here at OceanWP call <strong>Ocean Starboard</strong>. We promise right away, the changes are not just cosmetic or phonetic. The <strong>primary goal was to make the OceanWP Panel more organized, user-friendly</strong> and, you&#8217;ve guessed &#8211; add<strong> more useful options!</strong> </p>\n\n\n\n<p>We&#8217;re really excited to show you everything we&#8217;ve done and hope you&#8217;ll be excited for this release just as much as we are. So, let&#8217;s go through all the changes together, shall we?</p>\n\n\n\n<p>Before we begin, we made this Table of Content for you so that you can come back to this article in the future and use it as a starting point or guide for the brand new OceanWP Panel.</p>\n\n\n\n<p>Disclaimer: screenshots used in this article were based on the development versions of the OceanWP theme and Ocean plugins, so things may appear just a sprinkle different on your end.</p>\n\n\n\n<h2 id=\"table-context\">Table of Content:</h2>\n\n\n\n<ul><li>OceanWP Panel changes:<ul><li><a href=\"#panel-position\">Position (new)</a>,</li><li><a href=\"#panel-notifications\">Notifications Indicator (new)</a>,</li><li>Legacy Settings.</li></ul></li><li><a href=\"#panel-home-section\">Home Section (new)</a>:<ul><li><a href=\"#home-theme-status\">Theme Status (new)</a>,</li><li><a href=\"#home-quick-settings\">Quick Settings</a>,</li><li><a href=\"#home-download\">Download (new)</a>,<ul><li><a href=\"#home-child-theme\">OceanWP Child Theme download (new)</a>;</li></ul></li><li><a href=\"#home-love-corner\">The Love Corner (new)</a>,</li></ul></li><li>Customizer Section (new):<ul><li><a href=\"#customizer-search\">Customizer Search</a>,</li><li><a href=\"#customizer-reset\">Customizer Reset (new)</a>,</li><li><a href=\"#customizer-import-export\">Customizer Styling Import and Export</a>, </li><li><a href=\"#customizer-controls\">Customizer Controls (expanded)</a>,</li></ul></li><li><a href=\"#install-demos\">Install Demos</a></li><li><a href=\"#ocean-images\">Ocean Images</a></li><li><a href=\"#integration-section\">Integration Section</a>:<ul><li><a href=\"#integration-svg-support\">SVG Support (new)</a>,</li></ul></li><li><a href=\"#useful-plugins-section\">Useful Plugins Section</a> (new)</li><li><a href=\"#changelog-section\">Changelog Section (new)</a></li><li><a href=\"#extra-settings\">Extra Settings (new)</a></li><li><a href=\"#system-info-section\">System Info (new)</a></li><li><a href=\"#help-section\">Help Section (new)</a></li><li><a href=\"#theme-panel-plugins\">Theme Panel &amp; Ocean plugins</a></li><li><a href=\"#update-preparations\" data-type=\"internal\" data-id=\"#update-preparations\">How to prepare your website for updates</a></li><li><a href=\"#oceanwp-next\">What&#8217;s next for OceanWP?</a></li></ul>\n\n\n\n<h2>Theme Panel Becomes OceanWP Panel</h2>\n\n\n\n<p>Yes, yes, we know what you&#8217;re thinking.</p>\n\n\n\n<p>Not a glamorous change <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f600.png\" alt=\"😀\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> Besides the name change, which was done in order to make all settings more user-friendly, there&#8217;s really a lot more to it.</p>\n\n\n\n<p>Stay with us, as we&#8217;re about to give you a tour through all sections.</p>\n\n\n\n<h3 id=\"panel-position\">OceanWP Panel Position</h3>\n\n\n\n<p>OceanWP veteran users remember it was sometimes difficult to chase the panel down in the WordPress dashboard. With the latest OceanWP and Ocean Extra update, the OceanWP Panel will be positioned at the top of the WP dashboard, so you won&#8217;t miss it, ever again.</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"panel-notifications\">OceanWP Panel Notifications Indicator</h3>\n\n\n\n<p>Stay in the loop with our eye-catching notification indicator.</p>\n\n\n\n<p>Whenever an update is available or required to get access to new features, you will see a clear indicator &#8220;that there&#8217;s something going on&#8221;.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_103652281.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_103652281-1024x537.png\" alt=\"preview of the new OceanWP Panel notifications indicator\" class=\"wp-image-633631\" width=\"768\" height=\"403\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_103652281-1024x537.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_103652281-300x157.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_103652281-768x403.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_103652281.png 1448w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure></div>\n\n\n\n<p>Example, a theme update is available or you need to update a plugin to unlock new features. In a similar manner, you will need to update Ocean Elementor Widgets to unlock the new features we&#8217;ll talk about below.</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3>OceanWP Panel Legacy Settings</h3>\n\n\n\n<p>Some settings that come with Ocean Extra (My Library, for example) and additional Ocean plugins, such as the Ocean Custom Sidebar, Ocean Stick Anything, etc, will still be displayed as before &#8211; under the OceanWP Panel.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_105556888.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_105556888.png\" alt=\"preview of the OceanWP Theme Panel legacy settings\" class=\"wp-image-633634\" width=\"302\" height=\"319\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_105556888.png 403w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_105556888-284x300.png 284w\" sizes=\"(max-width: 302px) 100vw, 302px\" /></a></figure></div>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h2>New OceanWP Panel</h2>\n\n\n\n<p>And now to the best part. We present to you <strong>the new and very much improved OceanWP Panel</strong>.</p>\n\n\n\n<p>In this part we&#8217;ll go over all OceanWP Panel sections with you and explain all the options, changes and benefits.</p>\n\n\n\n<h3 id=\"panel-home-section\">OceanWP Panel: Home Section</h3>\n\n\n\n<p>Welcome and make yourself feel at home <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f609.png\" alt=\"😉\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p>The first section in the new OceanWP Panel is the Home section. We&#8217;ll start this tour with the very first, but also a new feature &#8211; the Theme Status.</p>\n\n\n\n<h4 id=\"home-theme-status\">Home Section: Theme Status</h4>\n\n\n\n<p>The Theme Status option is a<strong> brand new OceanWP update feature</strong>. </p>\n\n\n\n<p>Theme Status will inform all OceanWP users if there is a theme update available. It&#8217;s also one of the options that triggers our awesome notifications indicator we mentioned earlier.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111427046.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111427046.png\" alt=\"preview of the new OceanWP update feature called the Theme Status which displays whether a new OceanWP update is available\" class=\"wp-image-633641\" width=\"689\" height=\"162\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111427046.png 919w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111427046-300x71.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111427046-768x181.png 768w\" sizes=\"(max-width: 689px) 100vw, 689px\" /></a></figure></div>\n\n\n\n<p>You&#8217;ll also be able to check all feedback for the new release before updating. Of course, we&#8217;re referring to our already familiar document <a href=\"https://docs.oceanwp.org/article/776-known-bugs-issues#oceanwp\" target=\"_blank\" rel=\"noreferrer noopener\">Known Bugs &amp; Issues</a>. </p>\n\n\n\n<p>In this doc, we log all problems/issues users have encountered after updating to a specific theme or plugin version, and which we acknowledge to be a bug or a problem. Remember, <a href=\"https://oceanwp.org/support/\" data-type=\"page\" data-id=\"1236\">if you encounter a problem you believe might be a bug and not a specific user case, let us know about it</a> so we can throw ourselves at it immediately. Now, back to our features.</p>\n\n\n\n<p>In addition to the cute notification, you will also be able to<strong> update the theme directly from the OceanWP Panel, by simply clicking the &#8220;update now&#8221; link</strong>.</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h4 id=\"home-quick-settings\">Home Section: Quick Settings</h4>\n\n\n\n<p>The Quick Settings option is something we already had, but perhaps you never used it <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p>This section represents a <strong>quick access menu</strong>.<strong> Each menu item is a shortcut that leads to a specific Customizer panel or section</strong>, which saves you a bit of time (or clicks).</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111917925.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111917925.png\" alt=\"\" class=\"wp-image-633652\" width=\"728\" height=\"529\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111917925.png 970w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111917925-300x218.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_111917925-768x558.png 768w\" sizes=\"(max-width: 728px) 100vw, 728px\" /></a></figure></div>\n\n\n\n<p>Example, if you need to change the logo, just click on the Logo shortcut and it will open the Customizer with the Logo settings directly.</p>\n\n\n\n<p>The traditional way of accessing these settings is to open the Customizer, then Header settings, then the Logo section. To summarize, for this particular action you&#8217;re saving 2 additional steps.</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h4 id=\"home-download\">Home Section: Download</h4>\n\n\n\n<p><strong>The Download section is a completely new feature</strong> that comes with the latest OceanWP update. </p>\n\n\n\n<p>The first part of the Download section is &#8211; yours truly &#8211; the Ocean Extra plugin. The Ocean Extra plugin is 100% free but also required to unlock multiple features, as well as the functionality of the new OceanWP Panel. Basically, this has not changed <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p>On a quick side not, if you don&#8217;t plan to use the features mentioned, or additional Ocean plugins, be it free or pro, you are not required to use Ocean Extra plugin on your site. But we strongly recommend it because you&#8217;ll be missing out.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_122244139.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_122244139.png\" alt=\"\" class=\"wp-image-633665\" width=\"767\" height=\"385\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_122244139.png 1023w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_122244139-300x150.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_122244139-768x385.png 768w\" sizes=\"(max-width: 767px) 100vw, 767px\" /></a></figure></div>\n\n\n\n<p id=\"home-child-theme\">The second option is the <strong>OceanWP Child Theme download</strong>.</p>\n\n\n\n<p>As you already know, <strong>OceanWP has a fully functional and a ready-to-import and use child theme</strong>. When it comes to OceanWP, you <strong>don&#8217;t need to use third-party plugins to create a child theme</strong>.</p>\n\n\n\n<p>In fact, we advise you against it because some plugins tend to &#8220;lock&#8221; scripts and only call scripts that existed in a specific theme at the time of the creation of the child theme. This can cause issues with later updates, because your child theme may be reading old or non-existent scripts.</p>\n\n\n\n<p>However, downloading and importing a child theme can be a tedious task, especially for beginners. So, we&#8217;ve come up with a feature that allows you to <strong>install the OceanWP child theme with just 1-click, directly from the OceanWP Panel</strong>! And you can immediately see just how easy it is by inspecting the video below.</p>\n\n\n\n<figure class=\"wp-block-embed aligncenter is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<p class=\"responsive-video-wrap clr\"><iframe title=\"&#x1f525;&#x1f525;&#x1f525; Premier Preview: The Secret is OUT! OceanWP Theme Panel Receives a MAKEOVER &#x1f929;\" width=\"1200\" height=\"675\" src=\"https://www.youtube.com/embed/twUc0rHnBo0?start=127&#038;feature=oembed\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe></p>\n</div></figure>\n\n\n\n<p>The file you will be installing through this button is <a href=\"https://github.com/oceanwp/oceanwp-child-theme\" target=\"_blank\" rel=\"noreferrer noopener\">the same file that is publicly available on our GitHub account</a>. Likewise, when we make updates to the GitHub file, we&#8217;ll also replace the download file.</p>\n\n\n\n<p>If you&#8217;re a developer and wish to make changes to the child theme before installing it, for example, change the theme&#8217;s screenshot, you will need to:</p>\n\n\n\n<ul><li>download the OceanWP child theme file the traditional way,</li><li>use a software editing tool, like <a href=\"https://code.visualstudio.com\" target=\"_blank\" rel=\"noreferrer noopener\">Visual Studio Code</a>, then</li><li>upload the changed file in a ZIP file format to your website.</li></ul>\n\n\n\n<p>If you&#8217;re interested in installing the OceanWP child theme the &#8220;traditional&#8221; way, you can check our <a href=\"https://docs.oceanwp.org/article/90-sample-child-theme\" target=\"_blank\" rel=\"noreferrer noopener\">documentation on how to install a child theme</a> or you can check our <a href=\"https://www.youtube.com/watch?v=U4VIh1kVB90\" target=\"_blank\" rel=\"noreferrer noopener\">YouTube video on how to download and install the OceanWP child theme</a>.</p>\n\n\n\n<p>The one thing this process does not do is &#8220;migrate&#8221; your previous Customizer settings (if any), so this is something you&#8217;ll need to do manually <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> Luckily, we also made sure to make this process as easy as it can be. Check out this <a href=\"https://youtu.be/U4VIh1kVB90?t=170\" target=\"_blank\" rel=\"noreferrer noopener\">video part on how to import website styling  (Customizer settings) to your child theme</a>.</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h4 id=\"home-love-corner\">Home Section: The Love Corner</h4>\n\n\n\n<p>The name says it all &#8211; it&#8217;s a place to share and receive love <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p>In this area you have the option to:</p>\n\n\n\n<ul><li>share the rating and your experience with the OceanWP theme you love as much as we do,</li><li><a href=\"https://oceanwp.org/newsletter-subscription/\" data-type=\"page\" data-id=\"633584\">subscribe to our newsletter and be the first to know about updates, promotions and other news</a>,</li><li><a href=\"https://oceanwp.org/feature-requests/\" data-type=\"page\" data-id=\"611644\">request a feature you&#8217;d like to see in the OceanWP theme or any of the Ocean Extensions</a>,</li><li><a href=\"https://oceanwp.org/website-showcase/\" data-type=\"page\" data-id=\"623557\">share your website to be featured in our blog posts</a> <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></li></ul>\n\n\n\n<p>And while we&#8217;re at it, if you have an outstanding website based on OceanWP &#8211; don&#8217;t keep it a secret and <a href=\"https://oceanwp.org/website-showcase/\" data-type=\"page\" data-id=\"623557\">apply now for the OceanWP website showcase</a>! We&#8217;d love to see what you did and share with the rest of the OceanWP family.</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3>OceanWP Panel: Customizer Section</h3>\n\n\n\n<p><strong>The Customizer section is a brand new feature</strong> &#8211; or better yet &#8211; a brand new section, that comes with the latest OceanWP update.</p>\n\n\n\n<p>This <strong>section contains grouped features and options that are directly related to the WordPress Customizer</strong> (Appearance &gt; Customize).</p>\n\n\n\n<h4 id=\"customizer-search\">Customizer Section: Customizer Search</h4>\n\n\n\n<p>Customizer Search is an option we introduced at the and of 2021. Seems so far away, doesn&#8217;t it.</p>\n\n\n\n<p>We understand that <strong>browsing through the Customizer can sometimes be confusing or time-consuming</strong>. Even if you&#8217;re familiar with the OceanWP theme, you can sometimes forget where specific settings are located, especially if you don&#8217;t use them often.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_125832278.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_125832278-1024x255.png\" alt=\"preview of the OceanWP Panel Customizer Search control options\" class=\"wp-image-633667\" width=\"768\" height=\"191\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_125832278-1024x255.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_125832278-300x75.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_125832278-768x191.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_125832278.png 1193w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure></div>\n\n\n\n<p>That&#8217;s where the Customizer Search comes to the rescue.</p>\n\n\n\n<p>Activate the feature and it will become available in the Customizer. <strong>Use the search option to find all settings with ease and navigate directly to them</strong>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_130255862.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_130255862-1024x528.png\" alt=\"preview of the OceanWP Customizer Search option in action\" class=\"wp-image-633668\" width=\"768\" height=\"396\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_130255862-1024x528.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_130255862-300x155.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_130255862-768x396.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_130255862-1536x793.png 1536w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_130255862.png 1880w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure></div>\n\n\n\n<p><strong>The Customizer Search option is disabled by default</strong>. </p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h4 id=\"customizer-reset\">Customizer Section: Customizer Reset</h4>\n\n\n\n<p>And, here it is folks!</p>\n\n\n\n<p>Something you&#8217;ve been asking for &#8211; <strong>a handy feature all website developers will love as you do not have to use third-party plugins anymore</strong> <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_131617506.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_131617506.png\" alt=\"preview of the new OceanWP update feature, the Customizer Reset option.\" class=\"wp-image-633669\" width=\"620\" height=\"199\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_131617506.png 826w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_131617506-300x96.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_131617506-768x246.png 768w\" sizes=\"(max-width: 620px) 100vw, 620px\" /></a></figure></div>\n\n\n\n<p>The<strong> Customizer Reset option allows you to reset all Customizer settings to default theme settings</strong>. It&#8217;s extremely useful if you wish to switch to a different <a href=\"https://oceanwp.org/demos/\" data-type=\"page\" data-id=\"1403\">Ocean WordPress template demo</a> or you just don&#8217;t wish to find all the settings that came with a demo to correct them, and instead you&#8217;d like to make own changes from the scratch.</p>\n\n\n\n<p>What do you need to do? <strong>Just click on the Reset Customizer Settings button, confirm your action</strong> (as we don&#8217;t want any accidental clicks) <strong>and that&#8217;s it!</strong></p>\n\n\n\n<p>That being said, do not click for experiment purposes unless you&#8217;ve exported all your settings which you can import again.</p>\n\n\n\n<p>Which brings us to the next option &#8211; Customizer (Website Styling) Settings Import and Export</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h4 id=\"customizer-import-export\">Customizer Section: Customizer Styling Import and Export</h4>\n\n\n\n<p>If you&#8217;re a veteran OceanWP user, this is a feature you&#8217;re already familiar with. Except, this time, we placed it under the same roof with other Customizer options. We also made sure to add user and beginner-friendly descriptions.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_132020598.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_132020598.png\" alt=\"\" class=\"wp-image-633670\" width=\"761\" height=\"479\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_132020598.png 1014w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_132020598-300x189.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_132020598-768x484.png 768w\" sizes=\"(max-width: 761px) 100vw, 761px\" /></a></figure></div>\n\n\n\n<p>As there are no changes to the functionality or use of this feature, except for the location and the brand new styling, <a href=\"https://youtu.be/U4VIh1kVB90?t=170\" target=\"_blank\" rel=\"noreferrer noopener\">check out this video part to see how the export and import of Customizer styling functions</a>.</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h4 id=\"customizer-controls\">Customizer Section: Customizer Controls</h4>\n\n\n\n<p>Again, OceanWP long-time users are familiar with the Customizer Controls option. </p>\n\n\n\n<p>The <strong>Customizer Controls feature allows you to disable specific Customizer panels from loading</strong>. Its purpose is for the Customizer to load faster or not to load options you do not frequently need to use, therefore keep the Customizer organized and save time while editing.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142107067.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142107067.png\" alt=\"preview of the OceanWP panel Customizer controls option that allows you to disable specific customizer panel options from loading\" class=\"wp-image-633672\" width=\"764\" height=\"547\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142107067.png 1018w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142107067-300x215.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142107067-768x550.png 768w\" sizes=\"(max-width: 764px) 100vw, 764px\" /></a></figure></div>\n\n\n\n<p>However, <strong>the new OceanWP update expands this feature</strong>.</p>\n\n\n\n<p>Previously, <strong>OceanWP users had the opportunity only to disable default sections that were a part of the OceanWP theme</strong>, such us Top Bar, Typography, General, Header and several other panels.</p>\n\n\n\n<p>From now on, <strong>you can disable all panels generated by free and/or pro Ocean extensions</strong>. Example: Sticky Header, Sticky Footer, Modal Window, and more <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142543800.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142543800.png\" alt=\"preview of the additional Customizer control options for free and pro Ocean extensions, that come with the new OceanWP theme update\" class=\"wp-image-633673\" width=\"758\" height=\"614\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142543800.png 1010w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142543800-300x243.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_142543800-768x623.png 768w\" sizes=\"(max-width: 758px) 100vw, 758px\" /></a></figure></div>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"install-demos\">OceanWP Panel: Install Demos</h3>\n\n\n\n<p>One of your favorite features about OceanWP,<a href=\"https://oceanwp.org/demos/\"> full website WordPress templates</a>, has received a slight makeover and 2 (two) brand new functionalities.</p>\n\n\n\n<p>The old option, Install Demos (located under the OceanWP (Theme) Panel) now serves as a direct shortcut to the dedicated Install Demos section of the new OceanWP Panel. Naturally, this section can also be accessed directly through the OceanWP Panel.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_143241266.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_143241266-1024x755.png\" alt=\"preview of the OceanWP Panel Install Demos option makeover\" class=\"wp-image-633674\" width=\"768\" height=\"566\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_143241266-1024x755.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_143241266-300x221.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_143241266-768x566.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_143241266.png 1231w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure></div>\n\n\n\n<p>For all new OceanWP users, or those of you who rely on documentation, <strong>we added docs and links to video tutorials directly into this section</strong>. Whenever in doubt, you&#8217;ll have all the help you need at click&#8217;s reach <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p><strong>New Install Demos features include:</strong></p>\n\n\n\n<ul><li><strong>the Import Button</strong>, for user-friendliness (the import process will still function as before: if you click the demo image), and</li><li><strong>the option to disable/enable Install Demos</strong> whenever you do not use them.</li></ul>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144336250.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144336250-1024x325.png\" alt=\"preview of the new OceanWP update Install Demos feature to disable install demos functionality if not needed\n\" class=\"wp-image-633675\" width=\"768\" height=\"244\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144336250-1024x325.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144336250-300x95.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144336250-768x244.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144336250.png 1225w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure></div>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"ocean-images\">OceanWP Panel: Ocean Images</h3>\n\n\n\n<p>As announced in the <a href=\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/\" data-type=\"post\" data-id=\"631456\">Ocean Breeze updates release</a>, <strong>owners of the Business and Agency Core Extensions Bundle plans have the opportunity to enjoy the new features: direct image import from Freepik and icons import from Flaticon</strong>.</p>\n\n\n\n<p>In this section you&#8217;re allowed to enable all options per need and start working with royalty-free images on your website.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_153530016-1024x491.png\" alt=\"preview of the Ocean Images image and icons import feature and related settings\" class=\"wp-image-633683\" width=\"768\" height=\"368\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_153530016-1024x491.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_153530016-300x144.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_153530016-768x369.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_153530016.png 1215w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></figure></div>\n\n\n\n<p>Want to enjoy the Ocean Images feature? <a href=\"https://oceanwp.org/core-extensions-bundle/\" data-type=\"page\" data-id=\"36995\">Upgrade or purchase the Business or Agency Ocean Core Extensions Bundle</a> plan.</p>\n\n\n\n<p>Check out our<a href=\"https://oceanwp.org/royalty-free-images-and-icons-with-oceanwp/\" data-type=\"page\" data-id=\"631749\"> Royalty-free Images and Icons with OceanWP</a> page to find about the benefits, as well as how to use this feature.</p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"integration-section\">OceanWP Panel: Integration</h3>\n\n\n\n<p><strong>The Integration section</strong> is absolutely not a new feature, but it <strong>now comes as a standalone section</strong>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144637059.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144637059-1024x617.png\" alt=\"\" class=\"wp-image-633676\" width=\"768\" height=\"463\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144637059-1024x617.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144637059-300x181.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144637059-768x463.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_144637059.png 1241w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure></div>\n\n\n\n<p>Standard integration options include:</p>\n\n\n\n<ul><li>Google maps API (to use with Ocean Elementor Widgets: Google Map widget),</li><li>Google reCAPTCHA (to use with Ocean Elementor Widgets: Register widget), and</li><li>MailChimp API (to use with MailChimp widget from Ocean Extra or Newsletter Widget from Ocean Elementor Widget).</li></ul>\n\n\n\n<p id=\"integration-svg-support\"><strong>Brand NEW integration option includes</strong>:</p>\n\n\n\n<ul><li><strong>SVG file support.</strong></li></ul>\n\n\n\n<p>By default, <strong>you&#8217;re not allowed to upload SVG file types to your WordPress Media Library</strong>. Our new option will allow you to do just that, and <strong>upload SVG logos, icons and more</strong>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_145613003.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_145613003-1024x235.png\" alt=\"preview of the new OceanWP update feature that allows SVG support for the WordPress Media Library\" class=\"wp-image-633679\" width=\"768\" height=\"176\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_145613003-1024x235.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_145613003-300x69.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_145613003-768x176.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_145613003.png 1026w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure></div>\n\n\n\n<p>We made sure that <strong>all files you upload are completely sanitized</strong>. Even if you upload a file with malicious code, intentionally or by mistake, it will be entirely safe to use once it&#8217;s uploaded to your site.</p>\n\n\n\n<p>And the special <strong>benefit of the SVG support feature is &#8211; you don&#8217;t have to use any third-party plugins for this anymore</strong> <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p><strong>The SVG file support feature is disabled by default.</strong></p>\n\n\n\n<p class=\"has-text-align-center has-text-color has-small-font-size\" style=\"color:#1f4189\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"useful-plugins-section\">OceanWP Panel: Useful Plugins</h3>\n\n\n\n<p>The <strong>Useful Plugins sections</strong> is completely new. It <strong>contains options to install plugins we usually recommend or even use in our full website template demos</strong>, along with the description of the plugins&#8217; functionalities.</p>\n\n\n\n<p>Please note that these do not contain any affiliate or other marketing links &#8211; only WordPress repository links.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154625695-1024x598.png\" alt=\"preview of the OceanWP Panel useful plugins section for recommended plugins\" class=\"wp-image-633684\" width=\"768\" height=\"449\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154625695-1024x598.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154625695-300x175.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154625695-768x448.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154625695.png 1223w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></figure></div>\n\n\n\n<p>Aside from recommended plugins, you will <strong>also find a list of all Ocean free extensions that are available in the WordPress repository</strong>. Again, followed by information that describe our products best and with the option to install them from the WordPress repository directly through the new OceanWP panel.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154748476.png\" alt=\"preview of the OceanWP Panel useful plugins section for OceanWP free plugins\" class=\"wp-image-633685\" width=\"764\" height=\"689\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154748476.png 1018w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154748476-300x271.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_154748476-768x693.png 768w\" sizes=\"(max-width: 764px) 100vw, 764px\" /></figure></div>\n\n\n\n<p class=\"has-text-align-center\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"changelog-section\">OceanWP Panel: Changelog</h3>\n\n\n\n<p><strong>Seasoned WordPress users already know</strong> that, unlike with plugin updates, the <strong>theme&#8217;s changelog is not available for direct preview even though it&#8217;s present in the theme files</strong>.</p>\n\n\n\n<p>To allow our users to keep track of all the changes done, we&#8217;ve kept our <a href=\"https://docs.oceanwp.org/article/653-oceanwp-theme-changelog\" target=\"_blank\" rel=\"noreferrer noopener\">OceanWP Theme Changelog document</a>.  We&#8217;ll continue to update this document even after the new OceanWP Panel changes in case of users who don&#8217;t use Ocean Extra.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_155104019-1024x672.png\" alt=\"preview of the new OceanWP Panel feature that allows users to check the theme update changelog\" class=\"wp-image-633686\" width=\"768\" height=\"504\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_155104019-1024x672.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_155104019-300x197.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_155104019-768x504.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_155104019.png 1235w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></figure></div>\n\n\n\n<p>What more is there to say about this feature? <strong>The changelog is now available to you directly in the new OceanWP Panel</strong> <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p class=\"has-text-align-center\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"extra-settings\">OceanWP Panel: Extra Settings</h3>\n\n\n\n<p><strong>Extra Settings is a brand new feature or better yet &#8211; a pack of new features</strong>.</p>\n\n\n\n<p>From now on, all OceanWP users will be able to enable or disable all features that come with the Ocean Extra plugin and in any given moment.</p>\n\n\n\n<p>The Extra Settings option includes, but is not limited to:</p>\n\n\n\n<ul><li>disable/enable My Library (don&#8217;t want to create custom templates, shortcodes or other things to use across the theme? No problem),</li><li>disable/enable Metabox Settings (individual page, post, product or template settings),</li><li>disable/enable OceanWP WordPress widgets, entirely or individually, and more.</li></ul>\n\n\n\n<p class=\"has-text-align-center\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"system-info-section\">OceanWP Panel: System Info</h3>\n\n\n\n<p>The <strong>System Info is a brand new feature that will help us help you in case of any problems</strong>.</p>\n\n\n\n<p>The best part about it is that you don&#8217;t have to rely on third-party plugins or consult your hosting providers to retrieve this information.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_160742080-1024x777.png\" alt=\"preview of the new OceanWP Panel System Info feature\" class=\"wp-image-633687\" width=\"768\" height=\"583\" srcset=\"//oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_160742080-1024x777.png 1024w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_160742080-300x228.png 300w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_160742080-768x583.png 768w, //oceanwp.org/wp-content/uploads/2022/05/image_2022-05-04_160742080.png 1225w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></figure></div>\n\n\n\n<p><strong>With one click on the &#8220;Get System Report&#8221; button, you can generate the system info to copy/paste it for us </strong>on the WordPress forum, tickets or our <a href=\"https://www.facebook.com/groups/oceanwptheme/\" target=\"_blank\" rel=\"noreferrer noopener\">OceanWP Facebook Community</a> to review.</p>\n\n\n\n<p class=\"has-text-align-center\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h3 id=\"help-section\">OceanWP Panel: Help</h3>\n\n\n\n<p>The Help section serves as a <strong>shortcut to all OceanWP users in a need of a assistance</strong>.</p>\n\n\n\n<p>From the Help section, you can directly <strong>visit the OceanWP documentation</strong>, check out OceanWP YouTube video tutorials, or seek help, advice or guidance from us on the <a href=\"https://wordpress.org/support/theme/oceanwp/\" target=\"_blank\" rel=\"noreferrer noopener\">WordPress forum</a>, <a href=\"https://www.facebook.com/groups/oceanwptheme/\" target=\"_blank\" rel=\"noreferrer noopener\">Facebook Community Support group</a> or <a href=\"https://oceanwp.org/support/\" data-type=\"page\" data-id=\"1236\">submit a ticket if you&#8217;re a client</a>.</p>\n\n\n\n<p>If you&#8217;re new to WordPress or posting on the WordPress forum, please be advised that under the <a href=\"https://wordpress.org/support/guidelines/\" target=\"_blank\" rel=\"noreferrer noopener\">WordPress forum rules and guidelines</a> we are allowed to provide you with support for the OceanWP theme and free plugins only. For all other questions, come and join our Community or submit a ticket whenever you feel the need.</p>\n\n\n\n<p class=\"has-text-align-center\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h2 id=\"theme-panel-plugins\">OceanWP Panel Changes and Ocean Pro Extensions</h2>\n\n\n\n<p>The new OceanWP update brings also additional features to some of the Ocean Pro Extensions, like the Ocean Elementor Widgets or Ocean White Label.</p>\n\n\n\n<p>Ocean Elementor Widgets users will, from now on, have the option to disable any widget they don&#8217;t need or don&#8217;t want to use, so it won&#8217;t be loading in the Elementor Panel.</p>\n\n\n\n<p>Ocean White Label users will receive a bit more whitelabeling options, all in accordance with the new OceanWP Panel changes and the option to impress clients even more.</p>\n\n\n\n<p class=\"has-text-align-center\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h2>Ocean Plugins and OceanWP Panel Update</h2>\n\n\n\n<p>Our new update affects numerous free and pro Ocean extensions, similar to the <a href=\"https://oceanwp.org/blog/oceanwp-3-this-summer-is-all-about-vanilla-js/\" data-type=\"post\" data-id=\"616261\">update procedure when we released OceanWP 3 with Vanilla</a>.</p>\n\n\n\n<p>This means that you will need to update all plugins in order to start making use of all the latest features.</p>\n\n\n\n<p>The complete list of Ocean extensions affected by the OceanWP Panel makeover update:</p>\n\n\n\n<ol><li><strong>Ocean Extra</strong>, version 2.0.0,</li><li><strong>Ocean White Label</strong>, version 2.0.0 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Woo Popup</strong>, version 2.0.2 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Sticky Header</strong>, version 2.0.4 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Sticky Footer</strong>, version 2.0.3 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Cookie Notice</strong>, version 2.0.4 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Side Panel</strong>, version 2.0.4 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Footer Callout</strong>, version 2.0.2 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Portfolio</strong>, version 2.0.7 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Popup Login</strong>, version 2.1.1 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Gutenberg Blocks</strong>, version 1.1.1 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Pro Demos</strong>, version 1.3.1 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Elementor Widgets</strong>, version 2.2.1 (pro plugin and a part of the Ocean Core Extensions Bundle),</li><li><strong>Ocean Social Sharing</strong>, version 2.0.2 (free WordPress plugin),</li><li><strong>Ocean Product Sharing</strong>, version 2.0.3 (free WordPress plugin),</li><li><strong>Ocean Custom Sidebar</strong>, version 1.0.9 (free WordPress plugin),</li><li><strong>Ocean Modal Window</strong>, version 2.0.5 (free WordPress plugin).</li></ol>\n\n\n\n<p>Now that you know what plugins you&#8217;ll need to update, let&#8217;s go over some things you need to know before you start updating.</p>\n\n\n\n<p class=\"has-text-align-center\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h2 id=\"update-preparations\">OceanWP Update: How to Prepare Your Website</h2>\n\n\n\n<p>We all love updates and new features, but just can&#8217;t become fond of update preparations <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p>This is a major update, and although we keep recommending the same procedure over and over no matter how big or small an update is, we&#8217;ll do it again (just in case).</p>\n\n\n\n<p><strong>The correct update order is:</strong></p>\n\n\n\n<ol><li><strong>Update OceanWP</strong>&nbsp;via Appearance &gt; Themes, then</li><li><strong>Update all Ocean plugin</strong>s via Plugins &gt; Installed Plugins.</li></ol>\n\n\n\n<p><strong>Before you update, make sure you have taken necessary precaution measures.</strong></p>\n\n\n\n<h3>Test the Update Before Updating Your Live Site</h3>\n\n\n\n<p>The&nbsp;<strong>best practice is to test new changes on localhost, test or staging environments.</strong></p>\n\n\n\n<p>If you&nbsp;<strong>don’t have the option to test the updates anywhere, do not update immediately</strong>. Wait for a couple of days or even a week, until we’ve gathered feedback and made sure it’s safe for you to update.</p>\n\n\n\n<p>Remember, even if everything is fine, something can always go wrong during updates. After all, the bad thing about software is – it’s software.</p>\n\n\n\n<p><strong>Make sure to backup your websites properly</strong>. Do not rely on hosting backups alone,&nbsp;<strong><a href=\"https://www.wpbeginner.com/plugins/7-best-wordpress-backup-plugins-compared-pros-and-cons/\" target=\"_blank\" rel=\"noreferrer noopener\">take manual backups as well</a></strong>&nbsp;and store data in a safe place. If you’re using the&nbsp;<a href=\"https://oceanwp.org/blog/how-to-reset-wordpress-website-fix-mistakes/\">WordPress Reset plugin (not to be confused with WordPress Database Reset), you can also create website snapshots</a>.</p>\n\n\n\n<p><strong>Important: remember to purge cache thoroughly</strong>&nbsp;after the update, multiple times if necessary. The same goes for the CDN (cloud) cache.</p>\n\n\n\n<h3>Updating a Live Website</h3>\n\n\n\n<p><strong>Website update preparation To-Do list</strong>:</p>\n\n\n\n<ol><li>Before updating the theme,&nbsp;<strong><a href=\"https://www.wpbeginner.com/plugins/7-best-wordpress-backup-plugins-compared-pros-and-cons/\" target=\"_blank\" rel=\"noreferrer noopener\">create a backup of your website</a></strong>.</li><li>Place your website in the<strong>&nbsp;Maintenance mode</strong>.</li><li><strong>Update OceanWP theme</strong>&nbsp;to its latest version via&nbsp;<strong>Appearance &gt; Themes</strong>.</li><li><strong>Update all Ocean plugins</strong> to their latest versions via Plugins &gt; Installed Plugins.</li><li><strong>Clear browser, website or CDN (cloud) cache</strong>.</li><li><strong>Inspect</strong> your website.</li><li>If everything is in order, <strong>deactivate the Maintenance mode</strong>.</li><li><strong>Enjoy </strong>all new OceanWP updates and features!</li></ol>\n\n\n\n<p class=\"has-text-align-center\"><a href=\"#table-context\">#Back to the Table of Content</a></p>\n\n\n\n<h2 id=\"oceanwp-next\">What&#8217;s Next for OceanWP?</h2>\n\n\n\n<p>Believe it or not, <strong>we already have other delicacies cooking in the OceanWP kitchen of features</strong>.</p>\n\n\n\n<p>While we can&#8217;t reveal everything, one of the things we&#8217;ll also be working on is improving OceanWP theme and Ocean plugins descriptions, settings (we might move some) to make them user (and beginner) friendly at the max. We&#8217;ll also make sure to do our regular round of accessibility improvements.</p>\n\n\n\n<p>Regardless of our plans, we want to know what you think and we&#8217;ll here to listen to all your ideas and suggestions. Think there&#8217;s a feature, document, demo or a video OceanWP users would really need? Let us know about it by <a href=\"https://oceanwp.org/feature-requests/\" data-type=\"page\" data-id=\"611644\">filling out the OceanWP feature request form</a>.</p>\n\n\n\n<p>And remember, if you&#8217;re an <a href=\"https://oceanwp.org/oceanwp-partner-program/\" data-type=\"page\" data-id=\"631293\">Agency Bundle license owner, you can also influence our plans and collaborate with us more closely</a>.</p>\n\n\n\n<h2>LOS; INR. (Long or Short, I Never Read):</h2>\n\n\n\n<p>We are&nbsp;<strong>releasing major updates</strong>.</p>\n\n\n\n<p><strong>Take website backups, including manual backup, before updating OceanWP and Ocean plugins</strong>.</p>\n\n\n\n<p>The&nbsp;<strong>best practice is to test all updates on test, localhost or staging environments</strong>. If you don’t have the opportunity to test the updates, do not update immediately. Wait with the updates a couple of days until we’ve gathered feedback.</p>\n\n\n\n<p>In case of any problems or issues, inform us about possible bugs or submit tickets.</p>\n\n\n\n<p>But, in case you got till the end of the article, please, let us know what you think. We&#8217;d love to know how you fancy the new OceanWP Panel and all other changes that come with it <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p>PS don&#8217;t forget to check out the full premiere preview video of the Ocean Starboard and OceanWP Theme Panel Makeover and see some of the features live</p>\n\n\n\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<p class=\"responsive-video-wrap clr\"><iframe title=\"&#x1f525;&#x1f525;&#x1f525; Premier Preview: The Secret is OUT! OceanWP Theme Panel Receives a MAKEOVER &#x1f929;\" width=\"1200\" height=\"675\" src=\"https://www.youtube.com/embed/twUc0rHnBo0?feature=oembed\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe></p>\n</div></figure>\n\n\n\n<p> </p>\n<p style=\"margin: 0!important;padding: 26px 0 0!important;\"><a href=\"https://oceanwp.org/blog/oceanwp-theme-panel-makeover/\" target=\"_blank\" style=\"display: inline-block; padding: 10px 30px; color: #ffffff; background-color: #13aff0; border-radius: 30px; font-size: 13px; font-weight: bold; line-height: 1.4; text-transform: uppercase; text-align: center; text-decoration: none; letter-spacing: 1px;\">Read More</a></p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:59:\"https://oceanwp.org/blog/oceanwp-theme-panel-makeover/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"0\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:79:\"\n		\n		\n					\n		\n		\n		\n				\n		\n		\n		\n\n					\n										\n					\n					\n			\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"Ocean Breeze for Spring 2022: What’s New in OceanWP\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:71:\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:80:\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 01 Apr 2022 09:54:37 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:3:{i:0;a:5:{s:4:\"data\";s:11:\"Introducing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:6:\"Update\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:9:\"WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://oceanwp.org/?p=631456\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1302:\"<p style=\"display: block; padding: 0 0 24px; margin: 0 !important;\"><a href=\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/\" target=\"_blank\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/New-Project-2.png\" width=\"1200\" height=\"700\" title=\"Ocean Breeze for Spring 2022: What&#8217;s New in OceanWP\" alt=\"Ocean Breeze for Spring 2022: What&#8217;s New in OceanWP\" style=\"width: 100%!important;\"></a></p><p style=\"padding: 0 26px !important; margin: 0 !important;\">You need it. Most importantly, you deserve it. The new OceanWP update brings fresh features to enrich your content even without page builders. Enjoy refurbished Ocean Website Templates, royalty-free images, blocks, widgets, etc. For some of you, it&#8217;s already April 1st. But, we can assure you &#8211; it&#8217;s not a joke. Brand new OceanWP updates [&#8230;]<p style=\"margin: 0!important;padding: 26px 0 0!important;\"><a href=\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/\" target=\"_blank\" style=\"display: inline-block; padding: 10px 30px; color: #ffffff; background-color: #13aff0; border-radius: 30px; font-size: 13px; font-weight: bold; line-height: 1.4; text-transform: uppercase; text-align: center; text-decoration: none; letter-spacing: 1px;\">Read More</a></p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Lior Zelering\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:38369:\"<p style=\"display: block; padding: 0 0 24px; margin: 0 !important;\"><a href=\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/\" target=\"_blank\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/New-Project-2.png\" width=\"1200\" height=\"700\" title=\"Ocean Breeze for Spring 2022: What&#8217;s New in OceanWP\" alt=\"Ocean Breeze for Spring 2022: What&#8217;s New in OceanWP\" style=\"width: 100%!important;\"></a></p><p style=\"padding: 0 26px !important; margin: 0 !important;\">\n<p class=\"has-text-color has-medium-font-size\" style=\"color:#212121\">You need it. Most importantly, you deserve it. The new OceanWP update brings fresh features to enrich your content even without page builders. Enjoy refurbished Ocean Website Templates, royalty-free images, blocks, widgets, etc.</p>\n\n\n\n<p>For some of you, it&#8217;s already April 1st. But, we can assure you &#8211; it&#8217;s not a joke. Brand new OceanWP updates are already available, and we&#8217;re bringing you &#8211; you&#8217;ve guessed that right &#8211; MORE features!</p>\n\n\n\n<p>We&#8217;re calling this stack of updates the <strong>Ocean Breeze</strong>, <strong>as new options will hit your websites with the freshness you deserve</strong>.</p>\n\n\n\n<p>Without further ado, we&#8217;re presenting you the crisp and novel changes you can start applying to your website immediately.</p>\n\n\n\n<h2>Goodbye GDPR, Hello Ocean Breeze</h2>\n\n\n\n<p>Building and maintaining a business and a website is difficult as is, even without all the regulations website owners need to follow. For example, the most dreaded GDPR regulations.</p>\n\n\n\n<p>Google has the largest free font library available and it&#8217;s widely used. So, what&#8217;s wrong with that?</p>\n\n\n\n<p>Apparently, the <strong>traditional way of using Google fonts on your website, through embedding, allows Google to collect data on your website visitors</strong>. As you can imagine, this method can be characterized as a breach of GDPR.</p>\n\n\n\n<p><strong>The only way to circumvent this potentially legal-threat is to host Google fonts locally on your website</strong>. Whether it&#8217;s through a child theme or using third-party plugins, this process can be a drag.</p>\n\n\n\n<p>Therefore we&#8217;ve come up with a better solution for all OceanWP users! <strong>New OceanWP update brings you the option to host Google fonts on your website with a single click!</strong></p>\n\n\n\n<p>And the best part is &#8211; <strong>no font upload required!</strong></p>\n\n\n\n<h3>How does hosting Google fonts locally function with OceanWP?</h3>\n\n\n\n<p>First, in order to make sure you&#8217;re able to use the new feature, you need to <strong><a href=\"https://wordpress.org/themes/oceanwp\" target=\"_blank\" rel=\"noreferrer noopener\">update your OceanWP theme to 3.2.0+ version</a></strong> via <strong>Appearance &gt; Themes</strong>, in your WordPress dashboard. Important, if your WordPress is running on a server using PHP 7.2 version or below, <a href=\"https://docs.oceanwp.org/article/776-known-bugs-issues\" target=\"_blank\" rel=\"noreferrer noopener\">please upgrade to PHP 7.4+ or follow these guidelines</a>.</p>\n\n\n\n<p>For more server environment recommendations, <a href=\"https://wordpress.org/about/requirements/\" target=\"_blank\" rel=\"noreferrer noopener\">check out the WordPress system requirements</a>.</p>\n\n\n\n<p class=\"has-text-color\" style=\"color:#3d0075\"><strong>TIP:</strong> If you&#8217;re new to WordPress or OcenaWP, check out <a href=\"https://youtu.be/_2NDmIjmNEM\" target=\"_blank\" rel=\"noreferrer noopener\">our video tutorial on how to install the OceanWP theme to your WordPress website</a>.</p>\n\n\n\n<p>After the update, <strong>navigate to Customize &gt; Typography &gt; General</strong>, and check the option &#8220;<strong>Google fonts from site</strong>&#8220;, then publish changes.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full\"><img width=\"545\" height=\"477\" src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_214611.png\" alt=\"OceanWP update: how to host Google fonts locally with OceanWP\" class=\"wp-image-631459\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_214611.png 545w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_214611-300x263.png 300w\" sizes=\"(max-width: 545px) 100vw, 545px\" /></figure></div>\n\n\n\n<p>The <strong>new option will download all fonts you currently have set up or choose in the future through the OceanWP Customizer into a subfolder on your website</strong>. And this is it, folks &#8211; that&#8217;s all you need to do. <strong>All OceanWP fonts will load from your website</strong>.</p>\n\n\n\n<p>This is the example of one of our WordPress templates we&#8217;re currently redoing (more about that later), where the <a href=\"https://fonts.google.com/specimen/ABeeZee\" target=\"_blank\" rel=\"noreferrer noopener\">Google font ABeeZee</a> has been set through the OceanWP Customizer.</p>\n\n\n\n<div class=\"wp-block-columns\">\n<div class=\"wp-block-column\">\n<figure class=\"wp-block-image size-full\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_221459.png\"><img width=\"609\" height=\"424\" src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_221459.png\" alt=\"OceanWP update: preview of how Google fonts are loaded on a website in browser inspector\" class=\"wp-image-631460\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_221459.png 609w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_221459-300x209.png 300w\" sizes=\"(max-width: 609px) 100vw, 609px\" /></a><figcaption>Before applying new OceanWP Host Google fonts from site feature</figcaption></figure>\n</div>\n\n\n\n<div class=\"wp-block-column\">\n<figure class=\"wp-block-image size-full\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_221704.png\"><img width=\"664\" height=\"406\" src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_221704.png\" alt=\"\" class=\"wp-image-631461\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_221704.png 664w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_221704-300x183.png 300w\" sizes=\"(max-width: 664px) 100vw, 664px\" /></a><figcaption>After applying new OceanWP Host Google fonts from site feature</figcaption></figure>\n\n\n\n<p></p>\n</div>\n</div>\n\n\n\n<p>Unfortunately, the <strong>new OceanWP update regarding Google fonts will only be applied to the OceanWP theme, Ocean plugins, as well as plugins that inherit theme&#8217;s font settings</strong>. If third-party plugins embed their fonts, there&#8217;s nothing we can do about it. Unless, that particular plugin has the option to inherit theme fonts and settings.</p>\n\n\n\n<p>Hint: <a href=\"https://wordpress.org/plugins/elementor/\" target=\"_blank\" rel=\"noreferrer noopener\">Your favorite page builder, <strong>Elementor</strong></a><strong>, has the option to disable Elementor fonts and instead have Elementor inherit theme fonts and styling</strong>.</p>\n\n\n\n<h2>OceanWP Update Brings More Options to Website Admins &#8211; Hooray!</h2>\n\n\n\n<h3>Access blog post directly from the archives page</h3>\n\n\n\n<p>Your time is valuable. Save a click!</p>\n\n\n\n<p>Now you can <strong>access blog posts and edit them in WordPress directly through the archive pages</strong>, using the EDIT link. The link is available only to admin users.</p>\n\n\n\n<figure class=\"wp-block-image size-large\"><img width=\"1024\" height=\"651\" src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223118-1024x651.png\" alt=\"OceanWP update: preview of the new admin feature on how to access blog post edit page directly through archive pages\" class=\"wp-image-631466\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223118-1024x651.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223118-300x191.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223118-768x488.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223118.png 1181w\" sizes=\"(max-width: 1024px) 100vw, 1024px\" /></figure>\n\n\n\n<h2>Delete unwanted comments directly from the post&#8217;s comments section</h2>\n\n\n\n<p>For those of you who don&#8217;t have comment preapproval option enabled, this feature will definitely come in handy.</p>\n\n\n\n<p><strong>New OceanWP update allows you to delete any comment directly from the post&#8217;s comments section</strong>. The Delete option is available only to website admins.</p>\n\n\n\n<figure class=\"wp-block-image size-full\"><img width=\"1025\" height=\"256\" src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223646.png\" alt=\"OceanWP update: preview on how to use a new feature to delete comments directly from the comments section\" class=\"wp-image-631468\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223646.png 1025w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223646-300x75.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_223646-768x192.png 768w\" sizes=\"(max-width: 1025px) 100vw, 1025px\" /></figure>\n\n\n\n<p>However,<strong> the comment you removed is not gone forever yet</strong>. Find all comments you deleted in the Comment&#8217;s Trash section within your WP dashboard. From there, you will have the option to either restore the comment or remove permanently.</p>\n\n\n\n<h2>OceanWP Update Brings Less Page Builders Dependency and Brand New Post Title Styles</h2>\n\n\n\n<p>Page builders are awesome! And we love them!</p>\n\n\n\n<p>But <strong>designing a custom blog post title template merely because of the introductory header can be excruciating, especially for beginners</strong>. Often, the ability to use such options require some pro features as well. </p>\n\n\n\n<p>Ocean Breeze to the rescue! Meet the OceanWP 6 (six) <strong>new Post Title Styles</strong>.</p>\n\n\n\n<h3>Bedazzle your blog posts with Ocean Breeze</h3>\n\n\n\n<p>We present you the <strong>Intro, Cover, Card, Card Invert, Screen and Screen Invert Post Title Styles</strong>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232719.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232719-1024x528.png\" alt=\"OceanWP Update: preview of the new Intro Blog Post Title Style\" class=\"wp-image-631474\" width=\"768\" height=\"396\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232719-1024x528.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232719-300x155.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232719-768x396.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232719-1536x793.png 1536w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232719.png 1880w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a><figcaption>The Intro Post Title Style</figcaption></figure></div>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232757.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232757-1024x528.png\" alt=\"OceanWP Update: preview of the new Cover Blog Post Title Style\" class=\"wp-image-631475\" width=\"768\" height=\"396\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232757-1024x528.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232757-300x155.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232757-768x396.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232757-1536x793.png 1536w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232757.png 1880w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a><figcaption>The Cover Post Title Style</figcaption></figure></div>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232852.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232852-1024x528.png\" alt=\"OceanWP Update: preview of the new Card Blog Post Title Style\" class=\"wp-image-631476\" width=\"768\" height=\"396\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232852-1024x528.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232852-300x155.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232852-768x396.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232852-1536x793.png 1536w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_232852.png 1880w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a><figcaption>The Card Post Title Style</figcaption></figure></div>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_233006.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_233006-1024x528.png\" alt=\"OceanWP Update: preview of the new Screen Blog Post Title Style\" class=\"wp-image-631478\" width=\"768\" height=\"396\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_233006-1024x528.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_233006-300x155.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_233006-768x396.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_233006-1536x793.png 1536w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_233006.png 1880w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a><figcaption>The Screen Post Title Style</figcaption></figure></div>\n\n\n\n<h3>Additional Post Title Features</h3>\n\n\n\n<p>Additional <strong>new OceanWP Post Title Stylef features include 2 (two) Meta Styles to choose from: Stylish and Minimal</strong>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234346.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234346-1024x197.png\" alt=\"OceanWP Update: preview of the new stylish meta style for new Post Title Styles\" class=\"wp-image-631482\" width=\"768\" height=\"148\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234346-1024x197.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234346-300x58.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234346-768x148.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234346.png 1527w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a><figcaption>The Stylish Meta for the new OceanWP Post Title Style</figcaption></figure></div>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234759.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234759-1024x203.png\" alt=\"OceanWP Update: preview of the new minimal meta style for new Post Title Styles\" class=\"wp-image-631483\" width=\"768\" height=\"152\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234759-1024x203.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234759-300x60.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234759-768x152.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_234759.png 1526w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a><figcaption>The Minimal Meta for the new OceanWP Post Title Style</figcaption></figure></div>\n\n\n\n<p>The <strong>Ocean Post Title metas also include the options to choose the separator: None, Classic, Stylish and Modern</strong>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235300.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235300-1024x207.png\" alt=\"OceanWP Update: preview of the new meta separator style for new Post Title Styles\" class=\"wp-image-631484\" width=\"768\" height=\"155\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235300-1024x207.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235300-300x61.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235300-768x155.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235300.png 1529w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a><figcaption>The Stylish Meta Separator Style</figcaption></figure></div>\n\n\n\n<p>And just like you&#8217;re used to with OceanWP, the <strong>new update also brings you the options to choose which meta elements you&#8217;re going to display, as well as the order of those elements</strong>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235452.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235452-1024x244.png\" alt=\"OceanWP Update: preview of the new meta elements structure and visibility options\" class=\"wp-image-631485\" width=\"768\" height=\"183\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235452-1024x244.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235452-300x72.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235452-768x183.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235452-1536x366.png 1536w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235452.png 1865w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a><figcaption>Choose the order of new Post Title Styles meta elements and their visibility</figcaption></figure></div>\n\n\n\n<p><strong>Independent typography features are available via Customize &gt; Typography</strong>. The following options allow you to set the fonts and colors:</p>\n\n\n\n<ul><li>Blog Post Title Heading,</li><li>Blog Post Title Meta,</li><li>Blog Post Title Author, and</li><li>Blog Post Title Author Bio.</li></ul>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235730.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235730-1024x410.png\" alt=\"OceanWP Update: preview of the new typography settings for new Post Title Styles\" class=\"wp-image-631487\" width=\"768\" height=\"308\" srcset=\"//oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235730-1024x410.png 1024w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235730-300x120.png 300w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235730-768x307.png 768w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235730-1536x614.png 1536w, //oceanwp.org/wp-content/uploads/2022/03/image_2022-03-31_235730.png 1865w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<p>Update OceanWP theme to a 3.2.0+ version and start enjoying the new features Ocean Breeze update brings you. Happy blogging!</p>\n\n\n\n<h2>The Brilliant Stuff: OceanWP and Freepik Cooperation</h2>\n\n\n\n<h3>Goodbye Image Placeholders and Legal Questions, Welcome Ocean Website Templates with Royalty-free Images</h3>\n\n\n\n<p class=\"has-medium-font-size\"><strong>It is our greatest pleasure to announce that</strong>, from now on, <strong>all new Ocean Website Templates will come with images and icons included &#8211; royalty-free and without any attribution requirements, thanks to the OceanWP and Freepik cooperation</strong>!</p>\n\n\n\n<p>Images are important.</p>\n\n\n\n<p>And we understand how much it sucks when instead of images you get image placeholders instead. Sadly, this has been our usual practice until now. You can <a href=\"https://docs.oceanwp.org/article/791-website-demo-templates-image-disclaimer\" target=\"_blank\" rel=\"noreferrer noopener\">read all about it in our Images Disclaimer</a> about the How and the Why.</p>\n\n\n\n<p>That&#8217;s the reason <strong>we&#8217;ve been working relentlessly on finding the perfect solution for all OceanWP users and clients. And we finally did it!</strong></p>\n\n\n\n<p>But wait a minute. <strong>Why is this such a huge deal?</strong> Everyone else already delivers images with their content.</p>\n\n\n\n<p>We have been hearing that a lot. And yes, <strong>it would have been really easy for us to implement images and push the legal responsibility down to you, then hide behind a fine print</strong>. For us, <strong>it was simply not an option when it comes to our users</strong>.</p>\n\n\n\n<p>Instead, <strong>we wanted the real deal &#8211; real royalty-free images</strong>. <strong>No attribution, no fine print, no legal question,</strong> as <strong>we here at OceanWP are financing your rights to use the images and icons that will now make <a href=\"https://oceanwp.org/demos\" data-type=\"URL\" data-id=\"https://oceanwp.org/demos\">Ocean Website Templates</a> even more amazing</strong>, thanks to Freepik and Flaticon.</p>\n\n\n\n<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<figure class=\"wp-block-gallery aligncenter has-nested-images columns-default\">\n<figure class=\"wp-block-image size-medium\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/04/1841784.png\"><img width=\"300\" height=\"300\" data-id=\"631493\"  src=\"https://oceanwp.org/wp-content/uploads/2022/04/1841784-300x300.png\" alt=\"\" class=\"wp-image-631493\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/1841784-300x300.png 300w, //oceanwp.org/wp-content/uploads/2022/04/1841784-150x150.png 150w, //oceanwp.org/wp-content/uploads/2022/04/1841784-128x128.png 128w, //oceanwp.org/wp-content/uploads/2022/04/1841784-256x256.png 256w, //oceanwp.org/wp-content/uploads/2022/04/1841784.png 512w\" sizes=\"(max-width: 300px) 100vw, 300px\" /></a></figure>\n\n\n\n<figure class=\"wp-block-image size-medium\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/04/1841783.png\"><img width=\"300\" height=\"300\" data-id=\"631494\"  src=\"https://oceanwp.org/wp-content/uploads/2022/04/1841783-300x300.png\" alt=\"\" class=\"wp-image-631494\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/1841783-300x300.png 300w, //oceanwp.org/wp-content/uploads/2022/04/1841783-150x150.png 150w, //oceanwp.org/wp-content/uploads/2022/04/1841783-128x128.png 128w, //oceanwp.org/wp-content/uploads/2022/04/1841783-256x256.png 256w, //oceanwp.org/wp-content/uploads/2022/04/1841783.png 512w\" sizes=\"(max-width: 300px) 100vw, 300px\" /></a></figure>\n</figure>\n\n\n\n<p>How cool is that?</p>\n\n\n\n<h3>Refurbishing existing Ocean Website Templates</h3>\n\n\n\n<p>As we already mentioned, <strong>we are in a process of redesigning and refurbishing <a href=\"https://oceanwp.org/demos\" data-type=\"URL\" data-id=\"https://oceanwp.org/demos\">ALL existing Ocean Website Templates</a></strong>.</p>\n\n\n\n<p>This also means <strong>we are replacing existing images and icons</strong>, with those <strong>you will be able to keep and use if you choose to do so</strong>. High five!</p>\n\n\n\n<p>However, <strong>to avoid potential problems with SVG support, all icons</strong> in Ocean template demos <strong>will be replaced with PNG files instead</strong>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_004812.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_004812-1024x487.png\" alt=\"OceanWP Update: preview of the modernized Ocean WordPress website templates and new images feature\" class=\"wp-image-631499\" width=\"768\" height=\"365\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_004812-1024x487.png 1024w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_004812-300x143.png 300w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_004812-768x365.png 768w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_004812-1536x731.png 1536w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_004812.png 1845w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure></div>\n\n\n\n<p><strong>Find the list of currently available and renewed Ocean Website Templates using the &#8220;Freepik Images Included&#8221; filter</strong>.</p>\n\n\n\n<p>Once we redo all website templates, we will remove the filter to avoid confusion.</p>\n\n\n\n<p class=\"has-text-color\" style=\"color:#3d0075\"><strong>TIP:</strong> If you&#8217;re new to OceanWP and have not tried out our WordPress website templates yet, see how simple it is to <a href=\"https://youtu.be/8beTQrXkknQ\" target=\"_blank\" rel=\"noreferrer noopener\">import a fully operational template demo on your own</a> or <a href=\"https://youtu.be/rvW_HOYutT0\" target=\"_blank\" rel=\"noreferrer noopener\">get everything installed by Cloudways for you</a>.</p>\n\n\n\n<h3>Heads Up</h3>\n\n\n\n<p><strong>OceanWP users who have used our demo import in the past</strong> but for whom, for whatever reason,<strong> images</strong> DID NOT import directly to their website&#8217;s Media Library and instead were <strong>loading externally from the website demo in question may experience &#8220;image disappearance&#8221;</strong>.</p>\n\n\n\n<p>Please note, <strong>we do not have the power to alter your content</strong>. The thing is simple &#8211; <strong>we are removing old images completely</strong>.</p>\n\n\n\n<p><strong>When we permanently delete an image, the external links used by images on your website will no longer function</strong>, thus the images will &#8220;disappear&#8221;.</p>\n\n\n\n<p>We do apologize up front to everyone who will experience this, but:</p>\n\n\n\n<ol><li>We cannot prevent this, sadly.</li><li>Luckily, it&#8217;s for a much better cause because <strong>now you&#8217;ll be 100% sure it is safe to use images and icons that come with our templates</strong>.</li></ol>\n\n\n\n<p>And just when you think things can&#8217;t get any better and you can&#8217;t get more &#8211; with OceanWP there&#8217;s always MORE.</p>\n\n\n\n<h2>New Ocean Popup Login Security Features</h2>\n\n\n\n<h3>Enforce strong passwords usage upon user registration</h3>\n\n\n\n<p><strong>New OceanWP updates and <a href=\"https://docs.oceanwp.org/article/678-ocean-popup-login-changelog\" target=\"_blank\" rel=\"noreferrer noopener\">Ocean Popup Login version</a> enable you to enforce strong passwords usage</strong> upon user registration.</p>\n\n\n\n<p>Choose the options whether your users will need to combine their <strong>passwords with capital letters, special characters and/or numbers.</strong> The decision is only yours, and the new feature is disabled by default.</p>\n\n\n\n<p>You can also choose the option for <strong>users to use only passwords that are between 8 (eight) and 50 (fifty) characters long</strong>. Too little or too many required characters can also be potentially harmful just as equal.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_103749.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_103749.png\" alt=\"Ocean Popup Login plugin: preview of the new security features\" class=\"wp-image-631521\" width=\"227\" height=\"527\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_103749.png 303w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_103749-129x300.png 129w\" sizes=\"(max-width: 227px) 100vw, 227px\" /></a></figure></div>\n\n\n\n<h3>Limit Potentially Harmful Activity</h3>\n\n\n\n<p><strong>Limit activities such as entering an incorrect password multiple times or requesting the password reset link multiple times within the timeframe you assign</strong>. </p>\n\n\n\n<p>Such actions are usually related to bot scripts, and <strong>the new features will protect your website from potential harm.</strong> Disabled by default.</p>\n\n\n\n<p>If enabled, the new security feature will automatically be applied to login form, as well as the the default login pages: /wp-admin and /wp-login.php</p>\n\n\n\n<p>If triggered, the <strong>user / device that &#8220;hit the nerve&#8221; will be temporarily blocked from using these options</strong>: enter a password / request a password reset link.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_103906.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_103906.png\" alt=\"Ocean Popup Login plugin: preview of the new security features for limiting potentially harmful user activity\" class=\"wp-image-631522\" width=\"230\" height=\"410\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_103906.png 307w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_103906-169x300.png 169w\" sizes=\"(max-width: 230px) 100vw, 230px\" /></a></figure></div>\n\n\n\n<p><strong>Update your Ocean Popup Login plugin to the latest 2.1.0 version or <a href=\"https://oceanwp.org/core-extensions-bundle/\">upgrade with the Ocean Core Extensions Bundle and apply new security features to your website</a></strong>.</p>\n\n\n\n<h2>New Ocean Gutenberg Blocks</h2>\n\n\n\n<p><a href=\"https://docs.oceanwp.org/article/794-ocean-gutenberg-blocks-changelog\" target=\"_blank\" rel=\"noreferrer noopener\">The Ocean Gutenberg Blocks plugin</a> just got richer with 8 (eight) blocks:</p>\n\n\n\n<ul><li>Star Rating,</li><li>Recipe,</li><li>Modal Window,</li><li>Icon List,</li><li>Section,</li><li>Team,</li><li>Buttons, and</li><li>Circle Progress.</li></ul>\n\n\n\n<p><strong>Update your Ocean Gutenberg Blocks plugin to the latest 1.1.0 version or </strong><a href=\"https://oceanwp.org/core-extensions-bundle/\"><strong>upgrade with the Ocean Core Extensions Bundle and start building with all new blocks</strong>.</a></p>\n\n\n\n<h2>New Ocean Elementor Widgets</h2>\n\n\n\n<p><a href=\"https://docs.oceanwp.org/article/679-ocean-elementor-widgets-changelog\" target=\"_blank\" rel=\"noreferrer noopener\">The Ocean Elementor Widgets plugin</a> has added 5 (five) cool widgets to its already vast list of available tools:</p>\n\n\n\n<ul><li>Twitter Embed,</li><li>Twitter Timeline,</li><li>Click to Tweet,</li><li>Formidable Forms support, and</li><li>Fluent Forms support.</li></ul>\n\n\n\n<p><strong>Update your Ocean Elementor Widgets plugin to the latest 2.2.0 version or </strong><a href=\"https://oceanwp.org/core-extensions-bundle/\"><strong>upgrade with the Ocean Core Extensions Bundle and empower your Elementor page builder with these awesome widgets</strong>.</a></p>\n\n\n\n<h2>The Cherry on Top &#8211; Direct Image and Icons Import</h2>\n\n\n\n<p><strong>Owners of the Ocean Core Extensions Bundle Business and Agency plans</strong> will be exploring brand new features with the <a href=\"https://docs.oceanwp.org/article/670-ocean-pro-demos-changelog\" target=\"_blank\" rel=\"noreferrer noopener\">Ocean Pro Demos plugin</a>:</p>\n\n\n\n<ul><li>Direct Freepik image import, and</li><li>Direct Flaticon icons import.</li></ul>\n\n\n\n<p><strong>With no download limits</strong>, here at OceanWP we wanted to make sure <strong>your business flourishes as you spend less time searching for images and worry less about attribution and royalty.</strong></p>\n\n\n\n<h3>How does the new Ocean Images Import work?</h3>\n\n\n\n<p>First, <strong>you need to update your Ocean Pro Demos plugin to the latest 1.3.0 version and upgrade to the Business or Agency plan with the Ocean Core Extensions Bundle</strong>.</p>\n\n\n\n<p>After that, <strong>navigate to Theme Panel &gt; Ocean Images</strong>, in your WordPress dashboard.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_095958-1024x672.png\" alt=\"OceanWP Update: preview of the new Direct Image Import to a WordPress website feature\" class=\"wp-image-631517\" width=\"768\" height=\"504\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_095958-1024x672.png 1024w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_095958-300x197.png 300w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_095958-768x504.png 768w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_095958.png 1106w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></figure></div>\n\n\n\n<p>This will redirect you to the Integrations panel, where you can enable the Images module. <strong>The module is supported by the Ocean Core Extensions Bundle Business and Agency plans only.</strong></p>\n\n\n\n<p><strong>Enable the module you want and save changes</strong>. For the showcase purposes, we will enable the Freepik module and set the image download width to 1380px.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_100533.png\" alt=\"OceanWP Update: preview of the new Direct Image Import and how to apply image settings\" class=\"wp-image-631518\" width=\"646\" height=\"546\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_100533.png 861w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_100533-300x254.png 300w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_100533-768x649.png 768w\" sizes=\"(max-width: 646px) 100vw, 646px\" /></figure></div>\n\n\n\n<p>Once done, a <strong>new option &#8220;Browse Ocean Images&#8221; will appear under your Media</strong> option in the WordPress dashboard.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_101431-1024x415.png\" alt=\"OceanWP Update: preview of the new Direct Image Import and how the image import functions\" class=\"wp-image-631519\" width=\"768\" height=\"311\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_101431-1024x415.png 1024w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_101431-300x122.png 300w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_101431-768x312.png 768w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_101431-1536x623.png 1536w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_101431.png 1548w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></figure></div>\n\n\n\n<p><strong>Type in your search query, click on the image you want and find the image in your Media Library</strong>. That&#8217;s it!</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_102804-1024x419.png\" alt=\"OceanWP Update: preview of the new Direct Image Import and where to locate imported image files\" class=\"wp-image-631520\" width=\"768\" height=\"314\" srcset=\"//oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_102804-1024x419.png 1024w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_102804-300x123.png 300w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_102804-768x314.png 768w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_102804-1536x629.png 1536w, //oceanwp.org/wp-content/uploads/2022/04/image_2022-04-01_102804.png 1539w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></figure></div>\n\n\n\n<p><strong>You&#8217;re now equipped to design fabulous pages</strong>, just the way you always wanted.</p>\n\n\n\n<p>You can also disable the module whenever you don&#8217;t have the need to import images, so it won&#8217;t be running in the background. Images that you have already imported in the Media Library will remain unless you manually delete them.</p>\n\n\n\n<h2>OceanWP Update &#8211; Wrapping It Up</h2>\n\n\n\n<p><strong>The Ocean Breeze updates stack brings many new and really, really awesome features</strong>: single blog post Post Title Styles with multiple options you can choose from and settings to apply; Hosting Google fonts locally with a single click and without font files upload; Royalty-free images and renewed Ocean WordPress website templates; Ocean Popup Login security features; New blocks and widgets for Ocean Gutenberg Blocks and Ocean Elementor Widgets; and, the feature we&#8217;re most proud of &#8211; Direct Image and Icon Import for all Business and Agency Ocean Core Extensions Bundle plan owners.</p>\n\n\n\n<p><strong>We only want the best for all of you.</strong></p>\n\n\n\n<p>If you believe some things need additional attention or you require support when it comes to all the latest features, don&#8217;t forget to <a href=\"https://oceanwp.org/support/\" data-type=\"page\">open a support ticket</a> or <a href=\"https://oceanwp.org/pre-purchase-question/\" data-type=\"page\" data-id=\"182\">send us a prepurchase question</a>, and <a href=\"https://www.facebook.com/groups/oceanwptheme\" target=\"_blank\" rel=\"noreferrer noopener\">join our Facebook Community Support group</a> so we can hang out together. We&#8217;re here for you.</p>\n\n\n\n<p><strong>We hope you will enjoy exploring and applying all new OceanWP goodies!</strong> Let us know what you think in the comments section <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p>As you know, <strong>when it comes to OceanWP &#8211; there&#8217;s always more!</strong> So stay tuned for more updates. If you there&#8217;s a feature you&#8217;d like to see in OceanWP, <a href=\"https://oceanwp.org/feature-requests/\" data-type=\"page\" data-id=\"611644\">you can always send your suggestions</a>.</p>\n\n\n\n<p class=\"has-medium-font-size\"><strong>Happy website building!</strong></p>\n\n\n\n<p>PS <a href=\"https://oceanwp.org/core-extensions-bundle/\">Don&#8217;t forget to check out our Spring 2022 promo and get up to 50% OFF on the Ocean Core Extensions Bundle</a>.</p>\n\n\n\n<p>PPS If you&#8217;re still not sure about using WordPress or OceanWP, see<a href=\"https://oceanwp.org/install-wordpress-in-10-minutes/\" data-type=\"page\" data-id=\"629635\"> how easy is to install WordPress with the OceanWP theme, Ocean plugins and your Ocean Website Template of choice. It only takes 10 minutes or 10 steps, whichever measurements you prefe</a>r.</p>\n<p style=\"margin: 0!important;padding: 26px 0 0!important;\"><a href=\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/\" target=\"_blank\" style=\"display: inline-block; padding: 10px 30px; color: #ffffff; background-color: #13aff0; border-radius: 30px; font-size: 13px; font-weight: bold; line-height: 1.4; text-transform: uppercase; text-align: center; text-decoration: none; letter-spacing: 1px;\">Read More</a></p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:76:\"https://oceanwp.org/blog/ocean-breeze-spring-2022-whats-new-in-oceanwp/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"3\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:2;a:6:{s:4:\"data\";s:76:\"\n		\n		\n					\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n					\n			\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:67:\"How to Translate Your OceanWP and WooCommerce Store (the Quick Way)\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:68:\"https://oceanwp.org/blog/how-to-translate-oceanwp-woocommerce-store/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:77:\"https://oceanwp.org/blog/how-to-translate-oceanwp-woocommerce-store/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 19 Jan 2022 12:00:52 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:9:\"WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:8:\"Tutorial\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://oceanwp.org/?p=624466\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1338:\"<p style=\"display: block; padding: 0 0 24px; margin: 0 !important;\"><a href=\"https://oceanwp.org/blog/how-to-translate-oceanwp-woocommerce-store/\" target=\"_blank\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/12/how-to-translate-oceanwp-woocommerce-store.png\" width=\"1200\" height=\"700\" title=\"How to Translate Your OceanWP and WooCommerce Store (the Quick Way)\" alt=\"How to Translate Your OceanWP and WooCommerce Store (the Quick Way)\" style=\"width: 100%!important;\"></a></p><p style=\"padding: 0 26px !important; margin: 0 !important;\">Translating a website used to be time-consuming and expensive. But with a multilingual-ready theme like OceanWP and WPML’s new Translate Everything mode, you can automatically translate your entire store in a fraction of the time, even on the tightest budget. See how it all works in this tutorial video: Why You Should Translate Your WooCommerce [&#8230;]<p style=\"margin: 0!important;padding: 26px 0 0!important;\"><a href=\"https://oceanwp.org/blog/how-to-translate-oceanwp-woocommerce-store/\" target=\"_blank\" style=\"display: inline-block; padding: 10px 30px; color: #ffffff; background-color: #13aff0; border-radius: 30px; font-size: 13px; font-weight: bold; line-height: 1.4; text-transform: uppercase; text-align: center; text-decoration: none; letter-spacing: 1px;\">Read More</a></p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Allison Rivers\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:10268:\"<p style=\"display: block; padding: 0 0 24px; margin: 0 !important;\"><a href=\"https://oceanwp.org/blog/how-to-translate-oceanwp-woocommerce-store/\" target=\"_blank\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/12/how-to-translate-oceanwp-woocommerce-store.png\" width=\"1200\" height=\"700\" title=\"How to Translate Your OceanWP and WooCommerce Store (the Quick Way)\" alt=\"How to Translate Your OceanWP and WooCommerce Store (the Quick Way)\" style=\"width: 100%!important;\"></a></p><p style=\"padding: 0 26px !important; margin: 0 !important;\">\n<p>Translating a website used to be time-consuming and expensive. But with a multilingual-ready theme like OceanWP and WPML’s new <strong>Translate Everything</strong> mode, you can automatically translate your entire store in a fraction of the time, even on the tightest budget.</p>\n\n\n\n<p>See how it all works in this tutorial video:</p>\n\n\n\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<p class=\"responsive-video-wrap clr\"><iframe title=\"How to translate your OceanWP website with WPML (The quick and easy way)\" width=\"1200\" height=\"675\" src=\"https://www.youtube.com/embed/GxyPFzK634Q?feature=oembed\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe></p>\n</div></figure>\n\n\n\n<h2>Why You Should Translate Your WooCommerce Store</h2>\n\n\n\n<p>Thanks to continuous improvements in automatic translation, translating your online store can be one of the easiest ways to grow your business.</p>\n\n\n\n<h3>Reach New Customers</h3>\n\n\n\n<p>We all know ranking highly in search results draws in more customers. When you translate your site, you make it possible to <a href=\"https://wpml.org/documentation/support/multilingual-seo/\" target=\"_blank\" rel=\"noopener\">rank well in search results for multiple languages</a>. Without changing any of your products, conducting costly research, or increasing your ad spend, you can reach a whole new market.</p>\n\n\n\n<h3>Increase Sales</h3>\n\n\n\n<p>Once a potential customer lands on your site and finds the products they’re looking for, it’s important to make it easy to complete their purchase. Having a fully-translated checkout process reduces the chance of cart abandonment. More completed sales = more money in your bottom line.</p>\n\n\n\n<h3>Improve Trust</h3>\n\n\n\n<p>When customers find a company they can trust, they come back again and again. Providing services and products in a person’s native language demonstrates an interest and willingness to engage with them. Continuing to provide after-purchase support in the form of translated documentation can be the difference between a one-time purchaser and a lifelong customer and advocate.</p>\n\n\n\n<h2>The Best Tools for Translating Your Online Store Easily and Affordably</h2>\n\n\n\n<p>To get your online store up and running as soon as possible, be sure to use tools that allow you to build and translate your store quickly and easily. When you build a WooCommerce store using WPML and OceanWP, you can rest assured that everything will work as it should. This is thanks to WPML and OceanWP’s commitment to ongoing compatibility.</p>\n\n\n\n<h3>OceanWP &#8211; A Theme That’s Multilingual-Ready</h3>\n\n\n\n<p>If you want to translate your site, it’s important to use a multilingual-ready theme like OceanWP. Multilingual-ready means you can translate every part of the theme, from <a href=\"https://wpml.org/documentation/theme-compatibility/build-multilingual-wordpress-sites-using-oceanwp-theme-and-wpml/\" target=\"_blank\" rel=\"noopener\">pages, menus, and widgets</a>, to the <a href=\"https://wpml.org/documentation/theme-compatibility/build-multilingual-wordpress-sites-using-oceanwp-theme-and-wpml/oceanwp-extensions-compatibility-wpml/\" target=\"_blank\" rel=\"noopener\">many extensions you can use to customize your WooCommerce store</a>.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/12/1.translated-store-1024x576.png\" alt=\"translate woocommerce store\" class=\"wp-image-624468\" width=\"768\" height=\"432\" srcset=\"//oceanwp.org/wp-content/uploads/2021/12/1.translated-store-1024x576.png 1024w, //oceanwp.org/wp-content/uploads/2021/12/1.translated-store-300x169.png 300w, //oceanwp.org/wp-content/uploads/2021/12/1.translated-store-768x432.png 768w, //oceanwp.org/wp-content/uploads/2021/12/1.translated-store-1536x864.png 1536w, //oceanwp.org/wp-content/uploads/2021/12/1.translated-store.png 1920w\" sizes=\"(max-width: 768px) 100vw, 768px\" /><figcaption><em>A translated online store built with OceanWP</em></figcaption></figure></div>\n\n\n\n<h3>WPML &#8211; Translate Your Whole Site Automatically</h3>\n\n\n\n<p><a href=\"https://wpml.org/\" target=\"_blank\" rel=\"noopener\">WPML</a> is the most popular WordPress plugin for multilingual sites, and for good reason! The new <strong>Translate Everything</strong> mode, introduced in their latest release, allows you to:</p>\n\n\n\n<ul><li>Automatically translate your entire site in minutes;</li><li>Review the automatic translations yourself or hire a reviewer to make sure they sound natural and accurate;</li><li>Effortlessly keep your site’s translations up to date (they update automatically any time you edit your default content);</li><li>Choose from Google Translate, Microsoft Azure, or DeepL to power automatic translation;</li><li>Translate content created with page builders like Elementor.</li></ul>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/12/2.translating-site-automatically-1024x576.png\" alt=\"translate woocommerce store\" class=\"wp-image-624469\" width=\"768\" height=\"432\" srcset=\"//oceanwp.org/wp-content/uploads/2021/12/2.translating-site-automatically-1024x576.png 1024w, //oceanwp.org/wp-content/uploads/2021/12/2.translating-site-automatically-300x169.png 300w, //oceanwp.org/wp-content/uploads/2021/12/2.translating-site-automatically-768x432.png 768w, //oceanwp.org/wp-content/uploads/2021/12/2.translating-site-automatically-1536x864.png 1536w, //oceanwp.org/wp-content/uploads/2021/12/2.translating-site-automatically.png 1920w\" sizes=\"(max-width: 768px) 100vw, 768px\" /><figcaption><em>Sit back and relax as WPML automatically translates your site</em></figcaption></figure></div>\n\n\n\n<p>Although automatic translation isn’t completely free, WPML gets great discounts from automatic translation providers due to the high volume they translate every month. They then pass along those discounts directly to you. This means automatic translation is super affordable for even the most budget-conscious.</p>\n\n\n\n<p>Plus, with WPML, your translations are saved to your own database, not another plugin’s cloud. You own your translations yourself.</p>\n\n\n\n<h3>WooCommerce Multilingual &#8211; Translate Your Products and Add Multi-Currency Support</h3>\n\n\n\n<p>WooCommerce Multilingual is a free add-on plugin that comes with the purchase of a WPML Multilingual CMS or Agency account.</p>\n\n\n\n<p>It allows you to translate:</p>\n\n\n\n<ul><li>Products</li><li>Product attributes</li><li>Product variations</li><li>Cart and checkout pages</li></ul>\n\n\n\n<p>&#8230;and more.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/12/3.translation-options-for-products-1024x575.png\" alt=\"translate woocommerce site\" class=\"wp-image-624470\" width=\"768\" height=\"431\" srcset=\"//oceanwp.org/wp-content/uploads/2021/12/3.translation-options-for-products-1024x575.png 1024w, //oceanwp.org/wp-content/uploads/2021/12/3.translation-options-for-products-300x169.png 300w, //oceanwp.org/wp-content/uploads/2021/12/3.translation-options-for-products-768x432.png 768w, //oceanwp.org/wp-content/uploads/2021/12/3.translation-options-for-products-1536x863.png 1536w, //oceanwp.org/wp-content/uploads/2021/12/3.translation-options-for-products.png 1920w\" sizes=\"(max-width: 768px) 100vw, 768px\" /><figcaption><em>Deciding how to translate products in WCML’s setup wizard</em></figcaption></figure></div>\n\n\n\n<p>It also includes robust <a href=\"https://wpml.org/documentation/related-projects/woocommerce-multilingual/multi-currency-support-woocommerce/\" target=\"_blank\" rel=\"noopener\">multi-currency features</a> so you can display different currencies based on language or location. Plus, you can set exchange rates, custom shipping rates, and configure different payment gateways per currency.</p>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/12/4.multi-currency-settings-1024x576.png\" alt=\"translate woocommerce store\" class=\"wp-image-624471\" width=\"768\" height=\"432\" srcset=\"//oceanwp.org/wp-content/uploads/2021/12/4.multi-currency-settings-1024x576.png 1024w, //oceanwp.org/wp-content/uploads/2021/12/4.multi-currency-settings-300x169.png 300w, //oceanwp.org/wp-content/uploads/2021/12/4.multi-currency-settings-768x432.png 768w, //oceanwp.org/wp-content/uploads/2021/12/4.multi-currency-settings-1536x864.png 1536w, //oceanwp.org/wp-content/uploads/2021/12/4.multi-currency-settings.png 1920w\" sizes=\"(max-width: 768px) 100vw, 768px\" /><figcaption><em>Setting different currencies per language</em></figcaption></figure></div>\n\n\n\n<h2>Get Started with WPML</h2>\n\n\n\n<p>Start translating your WooCommerce store today with WPML and WooCommerce Multilingual. A <a href=\"https://wpml.org/purchase/\" target=\"_blank\" rel=\"noopener\">WPML Multilingual CMS or Agency account</a> includes everything you need to translate your entire site automatically and get your multilingual business up and running!</p>\n<p style=\"margin: 0!important;padding: 26px 0 0!important;\"><a href=\"https://oceanwp.org/blog/how-to-translate-oceanwp-woocommerce-store/\" target=\"_blank\" style=\"display: inline-block; padding: 10px 30px; color: #ffffff; background-color: #13aff0; border-radius: 30px; font-size: 13px; font-weight: bold; line-height: 1.4; text-transform: uppercase; text-align: center; text-decoration: none; letter-spacing: 1px;\">Read More</a></p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"https://oceanwp.org/blog/how-to-translate-oceanwp-woocommerce-store/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"5\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:3;a:6:{s:4:\"data\";s:78:\"\n		\n		\n					\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n					\n			\n		\n		\n\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:8:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"How to Create a Dynamic Appointment Website with OceanWP &amp; Crocoblock\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:93:\"https://oceanwp.org/blog/how-to-create-a-dynamic-appointment-website-with-oceanwp-crocoblock/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:102:\"https://oceanwp.org/blog/how-to-create-a-dynamic-appointment-website-with-oceanwp-crocoblock/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 12 Jan 2022 07:42:50 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:8:\"Tutorial\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:9:\"WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://oceanwp.org/?p=623861\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1398:\"<p style=\"display: block; padding: 0 0 24px; margin: 0 !important;\"><a href=\"https://oceanwp.org/blog/how-to-create-a-dynamic-appointment-website-with-oceanwp-crocoblock/\" target=\"_blank\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/dynamic-appointment-website-oceanwp-crocoblock-1.jpg\" width=\"1200\" height=\"700\" title=\"How to Create a Dynamic Appointment Website with OceanWP &#038; Crocoblock\" alt=\"How to Create a Dynamic Appointment Website with OceanWP &#038; Crocoblock\" style=\"width: 100%!important;\"></a></p><p style=\"padding: 0 26px !important; margin: 0 !important;\">Are you looking to build a dynamic appointment booking website? For those who think it’s hard to deploy, there’s good news – it’s not. Nowadays, you don’t need developers, coders, and designers. You just need the right tools. In this article, you’ll find out what makes a good appointment website. We’ll take you through the [&#8230;]<p style=\"margin: 0!important;padding: 26px 0 0!important;\"><a href=\"https://oceanwp.org/blog/how-to-create-a-dynamic-appointment-website-with-oceanwp-crocoblock/\" target=\"_blank\" style=\"display: inline-block; padding: 10px 30px; color: #ffffff; background-color: #13aff0; border-radius: 30px; font-size: 13px; font-weight: bold; line-height: 1.4; text-transform: uppercase; text-align: center; text-decoration: none; letter-spacing: 1px;\">Read More</a></p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:2:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:96:\"https://oceanwp.org/wp-content/uploads/2021/11/8-appointment-booking-plugin-setup-–-step-3.m4v\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:9:\"video/mp4\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:78:\"https://oceanwp.org/wp-content/uploads/2021/11/15-frontend-form-submission.m4v\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:9:\"video/mp4\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:9:\"Lana Miro\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:26789:\"<p style=\"display: block; padding: 0 0 24px; margin: 0 !important;\"><a href=\"https://oceanwp.org/blog/how-to-create-a-dynamic-appointment-website-with-oceanwp-crocoblock/\" target=\"_blank\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/dynamic-appointment-website-oceanwp-crocoblock-1.jpg\" width=\"1200\" height=\"700\" title=\"How to Create a Dynamic Appointment Website with OceanWP &#038; Crocoblock\" alt=\"How to Create a Dynamic Appointment Website with OceanWP &#038; Crocoblock\" style=\"width: 100%!important;\"></a></p><p style=\"padding: 0 26px !important; margin: 0 !important;\">\n<p>Are you looking to build a dynamic appointment booking website? For those who think it’s hard to deploy, there’s good news – it’s not. Nowadays, you don’t need developers, coders, and designers. You just need the right tools.</p>\n\n\n\n<p>In this article, you’ll find out what makes a good appointment website. We’ll take you through the whole site creation routine and hand out the tools we’ve used. In the end, you will figure out how to create a booking system using just OceanWP and several <a href=\"https://crocoblock.com/?ref=3457\" target=\"_blank\" rel=\"noreferrer noopener\">Crocoblock</a> plugins.</p>\n\n\n\n<h2>What Makes OceanWP &amp; Crocoblock Great Together</h2>\n\n\n\n<ul><li><strong>Visual site-building</strong>. Since Gutenberg is a visual block editor, website developers can save time switching back and forth between the admin panel and the front-end. It’ll suffice to create a page structure in the editor and ensure the front-end version looks the same.&nbsp;&nbsp;</li><li><strong>Fewer external plugins</strong>. Forget about separate plugins for custom post types, forms, booking management, custom fields, etc. To get the appointment functionality up and running, you’ll need two Crocoblock plugins only.&nbsp;</li><li><strong>No middlemen</strong>. It means that developers do not have to rely on designers. You can take a demo template by OceanWP, rework its layout as you like, and add Crocoblock dynamic functionality to it.</li></ul>\n\n\n\n<h2>Appointment Website 101</h2>\n\n\n\n<h3><strong>What is an appointment booking website?</strong></h3>\n\n\n\n<p>Appointment websites are service-providing platforms where it’s possible to book a particular time slot. Often, these websites offer pay-per-hour services.&nbsp;</p>\n\n\n\n<h3><strong>What do appointment websites look like?</strong></h3>\n\n\n\n<p>They can be one-page business card websites, where it is possible to book a particular service from a single provider. On the flip side, appointment websites can house multiple providers. In that case, the website has dedicated Provider and Service single pages and catalogs.</p>\n\n\n\n<h3><strong>Is it difficult to create an appointment website?</strong></h3>\n\n\n\n<p>Not anymore. These days, you can build a dynamic website from scratch using the right tools – a page builder you prefer, a theme provider, some dynamic content plugin, and the appointment booking core.</p>\n\n\n\n<h3><strong>What are the main appointment website elements?</strong></h3>\n\n\n\n<p>These would be a theme, custom post types, an appointment booking engine, booking forms, Provider pages, Service pages, catalog pages, and listings.</p>\n\n\n\n<h3><strong>Can I add multiple providers to the same website?</strong></h3>\n\n\n\n<p>Why not? Several professionals can provide one service. Make sure to assign a provider to the service to avoid confusion at the stage of filling the booking form.</p>\n\n\n\n<h2>Appointment Website: Basic Preparations</h2>\n\n\n\n<p>We decided to omit the early-stage preps like getting a domain name and choosing a hosting provider. Instead, we’ll focus on the essence. For those who want to delve into detail, there’s a super informative video by Ferdy Korpershoek.</p>\n\n\n\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<p class=\"responsive-video-wrap clr\"><iframe title=\"Make An Appointment Booking Website Using JetAppointment\" width=\"1200\" height=\"675\" src=\"https://www.youtube.com/embed/JFwmE9T5Fmo?feature=oembed\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe></p>\n</div></figure>\n\n\n\n<h2>Get Crocoblock plugins</h2>\n\n\n\n<p>Crocoblock introduced its <a href=\"https://crocoblock.com/blog/crocoblock-launches-custom-subscription-plans/?ref=3457\" target=\"_blank\" rel=\"noopener\">updated pricing</a> six months ago. What makes it cool? It’s the <strong>Custom subscription</strong> plan. Now, to create a workable appointment website, you won’t have to spend much. Go to the <a href=\"https://crocoblock.com/pricing/?ref=3457\" target=\"_blank\" rel=\"noopener\">Crocoblock pricing</a> page, unfold the <strong>JetPlugins </strong>tab, and add the necessary plugins to the cart.</p>\n\n\n\n<p>You’ll need <a href=\"https://crocoblock.com/plugins/jetengine-gutenberg/?ref=3457\" target=\"_blank\" rel=\"noopener\"><strong>JetEngine</strong></a><strong> </strong>– a dynamic content plugin that allows creating Custom Post Types – and <a href=\"https://crocoblock.com/plugins/jetappointment/?ref=3457\" target=\"_blank\" rel=\"noopener\"><strong>JetAppointment</strong></a><strong> </strong>– a service booking WordPress plugin.</p>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/1-Crocoblock-shopping-cart.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/1-Crocoblock-shopping-cart-1024x472.png\" alt=\"screenshot of jetengine and jetappointment plugins in the shopping cart, necessary for appointment website\" class=\"wp-image-623868\" width=\"768\" height=\"354\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/1-Crocoblock-shopping-cart-1024x472.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/1-Crocoblock-shopping-cart-300x138.png 300w, //oceanwp.org/wp-content/uploads/2021/11/1-Crocoblock-shopping-cart-768x354.png 768w, //oceanwp.org/wp-content/uploads/2021/11/1-Crocoblock-shopping-cart.png 1172w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<p>Such a custom package will cost you $45/year for one website and $93/year for unlimited websites. Plus, you’ll be getting plugin updates and quality support for one whole year. Upload these plugins to the WordPress dashboard and get them ready for further work.</p>\n\n\n\n<h3>Install the OceanWP theme and choose a website template</h3>\n\n\n\n<p>There are two ways to install the core theme – the traditional one and the Crocoblock way.</p>\n\n\n\n<h4>Installing the OceanWP theme the traditional way</h4>\n\n\n\n<p>Go to <strong><em>Appearance &gt; Themes</em></strong> in the WordPress admin panel and press “<strong>Add New</strong>.” You can use the search bar to locate the theme. Once found, install OceanWP and activate it.</p>\n\n\n\n<figure class=\"wp-block-image size-full is-resized\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/2-OceanWP-theme-in-the-popular-themes.png\" alt=\"Screenshot of installing the OceanWP theme for the appointment website\" class=\"wp-image-623867\" width=\"729\" height=\"381\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/2-OceanWP-theme-in-the-popular-themes.png 972w, //oceanwp.org/wp-content/uploads/2021/11/2-OceanWP-theme-in-the-popular-themes-300x157.png 300w, //oceanwp.org/wp-content/uploads/2021/11/2-OceanWP-theme-in-the-popular-themes-768x401.png 768w\" sizes=\"(max-width: 729px) 100vw, 729px\" /></figure>\n\n\n\n<h4>Installing the OceanWP theme the Crocoblock way</h4>\n\n\n\n<p>Use the Crocoblock <a href=\"https://crocoblock.com/blog/magic-of-website-building-install-crocoblock-with-wizard/?ref=3457\" target=\"_blank\" rel=\"noreferrer noopener\">Installation Wizard</a>. It can be downloaded from the Crocoblock account and then used to install OceanWP.</p>\n\n\n\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<p class=\"responsive-video-wrap clr\"><iframe title=\"How to install Crocoblock |  Installation Wizard\" width=\"1200\" height=\"675\" src=\"https://www.youtube.com/embed/Eok2CflNQI8?start=49&#038;feature=oembed\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe></p>\n</div></figure>\n\n\n\n<p>To choose a demo template, go to <strong><em>Theme Panel &gt; Install Demos</em></strong> and pick the one you need. We’ll use the <a href=\"https://gym.oceanwp.org/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>Gym </strong>demo template</a> today. Remember that a demo cannot work as intended if some of the <a href=\"https://oceanwp.org/extension/category/premium/\">premium extensions</a> are missing. You may want to get them first.</p>\n\n\n\n<figure class=\"wp-block-image size-full\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/3-gym-demo-template-installation.png\"><img width=\"596\" height=\"658\" src=\"https://oceanwp.org/wp-content/uploads/2021/11/3-gym-demo-template-installation.png\" alt=\"OceanWP core and premium extensions in a Gym demo\" class=\"wp-image-623866\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/3-gym-demo-template-installation.png 596w, //oceanwp.org/wp-content/uploads/2021/11/3-gym-demo-template-installation-272x300.png 272w\" sizes=\"(max-width: 596px) 100vw, 596px\" /></a></figure>\n\n\n\n<p>As soon as the demo template installation is completed, you are ready to build a dynamic appointment website.</p>\n\n\n\n<h2>Building a Custom Appointment Website</h2>\n\n\n\n<p>Inside every appointment and event scheduling website, there are custom post types for <strong>Providers </strong>and <strong>Services</strong>, automated appointment booking core, calendar integration, and a booking form. These are essential elements, but you are at liberty to extend the list however you like.</p>\n\n\n\n<p>Now, let’s put JetEngine and JetAppointment plugins to good use.</p>\n\n\n\n<h3>Custom Post Types creation</h3>\n\n\n\n<p>Why do you need to create a CPT? They will help you organize the website posts – services to services, providers to providers. Afterward, these posts can be easily interrelated to let the website users book a particular service from those who provide it.</p>\n\n\n\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<p class=\"responsive-video-wrap clr\"><iframe title=\"How to use Gutenberg editor for Custom Post Types\" width=\"1200\" height=\"675\" src=\"https://www.youtube.com/embed/Ytyc4XXlEYc?feature=oembed\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe></p>\n</div></figure>\n\n\n\n<p>Follow the <strong><em>JetEngine &gt; Post Types &gt; Add New</em></strong> pathway in the WordPress admin panel to create a CPT. Remember that you need two post types – one for Providers and one for Services. Since we are building a Gym website, the post types will be named <strong>Trainers </strong>and <strong>Workouts </strong>accordingly.&nbsp;</p>\n\n\n\n<p>Add the necessary meta fields to both post types. For the <strong>Trainers CPT</strong>, these could be:</p>\n\n\n\n<ul><li>photo;</li><li>short bio;</li><li>work experience;</li><li>achievements;</li><li>gallery;</li><li>contacts.</li></ul>\n\n\n\n<figure class=\"wp-block-image size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/4-trainers-CPT-meta-fields.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/4-trainers-CPT-meta-fields.png\" alt=\"meta fields added to the trainers custom post type for the appointment website\" class=\"wp-image-623865\" width=\"687\" height=\"569\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/4-trainers-CPT-meta-fields.png 916w, //oceanwp.org/wp-content/uploads/2021/11/4-trainers-CPT-meta-fields-300x249.png 300w, //oceanwp.org/wp-content/uploads/2021/11/4-trainers-CPT-meta-fields-768x636.png 768w\" sizes=\"(max-width: 687px) 100vw, 687px\" /></a></figure>\n\n\n\n<p>For the <strong>Workouts CPT</strong>, the meta fields can be as follows:</p>\n\n\n\n<ul><li>gallery;</li><li>description;</li><li>maximum capacity;</li><li>equipment;</li><li>health benefits.</li></ul>\n\n\n\n<figure class=\"wp-block-image size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/5-workouts-CPT-meta-fields.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/5-workouts-CPT-meta-fields.png\" alt=\"meta fields added to the workouts custom post type for an appointment website\" class=\"wp-image-623864\" width=\"684\" height=\"524\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/5-workouts-CPT-meta-fields.png 912w, //oceanwp.org/wp-content/uploads/2021/11/5-workouts-CPT-meta-fields-300x230.png 300w, //oceanwp.org/wp-content/uploads/2021/11/5-workouts-CPT-meta-fields-768x588.png 768w\" sizes=\"(max-width: 684px) 100vw, 684px\" /></a></figure>\n\n\n\n<p class=\"has-background\" style=\"background-color:#f9f9f9\"><strong>Quick tip</strong>: JetEngine has got a useful <a href=\"https://crocoblock.com/blog/jetengine-glossary-feature-explained/?ref=3457\" target=\"_blank\" rel=\"noopener\"><strong>Glossary feature</strong></a>. It allows you to create a set of meta fields and use them as options for Custom Post Types. </p>\n\n\n\n<p>Next, add as many posts to the newly-created Post Types as necessary. To do so, mouse over the CPT name in the WordPress admin panel and then press “<strong>Add New</strong>.”</p>\n\n\n\n<p>Once done, you may proceed to configure the JetAppointment plugin.</p>\n\n\n\n<h3>JetAppointment setup</h3>\n\n\n\n<p>It is not that difficult to set the appointment booking plugin by Crocoblock. All you have to do is run the Setup Wizard and complete four installation steps.</p>\n\n\n\n<p>Go to <strong><em>Appointments &gt; Set Up</em></strong> in the admin panel. You’ll be taken to the <strong>JetPlugins Settings</strong> page, namely to the JetAppointment’s <strong>Set Up</strong> tab. Click on the similarly named button to run the Wizard.</p>\n\n\n\n<ul><li><strong>Step 1</strong>. You need to pick the Services and Providers post types in the corresponding drop-down selects. Don’t forget to enable the <strong>Add providers</strong> toggle; otherwise, the field won’t appear. Click “<strong>Next</strong>.”</li></ul>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/6-appointment-setup-step-1.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/6-appointment-setup-step-1-1024x542.png\" alt=\"screenshot of appointment booking plugin setup – step 1\" class=\"wp-image-623863\" width=\"768\" height=\"407\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/6-appointment-setup-step-1-1024x542.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/6-appointment-setup-step-1-300x159.png 300w, //oceanwp.org/wp-content/uploads/2021/11/6-appointment-setup-step-1-768x406.png 768w, //oceanwp.org/wp-content/uploads/2021/11/6-appointment-setup-step-1.png 1040w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<ul><li><strong>Step 2</strong>. Here, you can see the required database table columns. Continue the installation if you don’t feel like adding more columns. We’ll add Phone and Comments.</li></ul>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/7-appointment-setup-step-2.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/7-appointment-setup-step-2-1024x818.png\" alt=\"screenshot of appointment booking plugin setup – step 2\" class=\"wp-image-623876\" width=\"768\" height=\"614\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/7-appointment-setup-step-2-1024x818.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/7-appointment-setup-step-2-300x240.png 300w, //oceanwp.org/wp-content/uploads/2021/11/7-appointment-setup-step-2-768x613.png 768w, //oceanwp.org/wp-content/uploads/2021/11/7-appointment-setup-step-2.png 1032w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<ul><li><strong>Step 3</strong>. This step is dedicated to configuring the service and provider-specific details like duration, buffer time, days off, working hours, etc. For a detailed step overview, please check out this <a href=\"https://youtu.be/ykMb0LFcMIo?t=53\" target=\"_blank\" rel=\"noreferrer noopener\">video tutorial</a>.</li></ul>\n\n\n\n<figure class=\"wp-block-video\"><video controls src=\"https://oceanwp.org/wp-content/uploads/2021/11/8-appointment-booking-plugin-setup-–-step-3.m4v\"></video></figure>\n\n\n\n<ul><li><strong>Step 4</strong>. Lastly, you can integrate <strong>WooCommerce </strong>and create two appointment booking forms. The available options include “JetEngine Forms” and “JetFormBuilder.” The first is intended for Elementor, and the second is for Gutenberg.</li></ul>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/9-appointment-setup-step-4.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/9-appointment-setup-step-4-1024x637.png\" alt=\"screenshot of an appointment booking plugin setup – step 4\" class=\"wp-image-623875\" width=\"768\" height=\"478\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/9-appointment-setup-step-4-1024x637.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/9-appointment-setup-step-4-300x187.png 300w, //oceanwp.org/wp-content/uploads/2021/11/9-appointment-setup-step-4-768x477.png 768w, //oceanwp.org/wp-content/uploads/2021/11/9-appointment-setup-step-4.png 1031w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<p>Press the <strong>“Finish”</strong> button to finalize the setup.</p>\n\n\n\n<figure class=\"wp-block-image size-full is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/10-appointment-setup-completed.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/10-appointment-setup-completed.png\" alt=\"screenshot of the appointment setup final screen\" class=\"wp-image-623874\" width=\"758\" height=\"560\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/10-appointment-setup-completed.png 1010w, //oceanwp.org/wp-content/uploads/2021/11/10-appointment-setup-completed-300x222.png 300w, //oceanwp.org/wp-content/uploads/2021/11/10-appointment-setup-completed-768x567.png 768w\" sizes=\"(max-width: 758px) 100vw, 758px\" /></a></figure>\n\n\n\n<p>Before you edit the appointment booking forms, you need to interrelate the Providers and Services posts. Open a Provider’s post and find the <strong>Related Services </strong>tab in the left sidebar. Type three characters into that text area and pick the necessary service once the system suggests it.</p>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/11-add-workouts-to-trainer.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/11-add-workouts-to-trainer-1024x683.png\" alt=\"apply services to the provider post\" class=\"wp-image-623873\" width=\"768\" height=\"512\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/11-add-workouts-to-trainer-1024x683.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/11-add-workouts-to-trainer-300x200.png 300w, //oceanwp.org/wp-content/uploads/2021/11/11-add-workouts-to-trainer-768x512.png 768w, //oceanwp.org/wp-content/uploads/2021/11/11-add-workouts-to-trainer.png 1226w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<p>Repeat the procedure as many times as there are dedicated Provider posts. Once done, you are ready to play around with the forms.</p>\n\n\n\n<h2>JetFormBuilder and form addition to pages</h2>\n\n\n\n<p>What’s excellent about Crocoblock products is that some are actually free. Take <a href=\"https://crocoblock.com/plugins/jetformbuilder/\" target=\"_blank\" rel=\"noopener\">JetFormBuilder</a>. It’s a WordPress form-building plugin allowing site developers to build dynamic forms in the Gutenberg page editor. You can download the plugin, activate it, and use it right away.</p>\n\n\n\n<p>Go to <strong><em>JetFormBuilder &gt; Forms</em></strong>. The two generated forms will be there. We’ll have to edit them since we added two columns to the DB table during the appointment setup. Now, we need to add the necessary meta fields to the form and post-submit actions.</p>\n\n\n\n<p>Click to edit the <strong>Static Page Booking Form</strong> and add the missing meta fields by drag-n-dropping the corresponding Gutenberg blocks. We’ll need two – a <strong>Text Field </strong>and a <strong>Textarea Field </strong>– to add the Phone and Comment fields.</p>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/12-form-editing-in-gutenberg.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/12-form-editing-in-gutenberg-1024x729.png\" alt=\"adding text field blocks to the appointment booking form\" class=\"wp-image-623872\" width=\"768\" height=\"547\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/12-form-editing-in-gutenberg-1024x729.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/12-form-editing-in-gutenberg-300x213.png 300w, //oceanwp.org/wp-content/uploads/2021/11/12-form-editing-in-gutenberg-768x546.png 768w, //oceanwp.org/wp-content/uploads/2021/11/12-form-editing-in-gutenberg.png 1130w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<p>Next, you need to input these fields into the Post Submit Actions. To do so, press the <strong>JetForm </strong>tab, scroll down to the <strong>Post Submit Actions </strong>section, and click on the pencil icon below the action name. You’ll see a Post Submit Action editing window, where you are supposed to select the form fields where they belong.</p>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/13-post-submit-action-setup.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/13-post-submit-action-setup-1024x721.png\" alt=\"setting up the Insert Appointment post-submit action\" class=\"wp-image-623871\" width=\"768\" height=\"541\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/13-post-submit-action-setup-1024x721.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/13-post-submit-action-setup-300x211.png 300w, //oceanwp.org/wp-content/uploads/2021/11/13-post-submit-action-setup-768x541.png 768w, //oceanwp.org/wp-content/uploads/2021/11/13-post-submit-action-setup.png 1130w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<p>Besides that, you can set up WooCommerce integration here, provided that you’ve enabled it at Step 4. Once done, click the “<strong>Update</strong>” button.</p>\n\n\n\n<p class=\"has-background\" style=\"background-color:#f9f9f9\"><strong>Quick tip</strong>. Feel free to add several <a href=\"https://jetformbuilder.com/features/post-submit-action-controls-overview/\" target=\"_blank\" rel=\"noreferrer noopener\">Post Submit Actions</a> at once but ensure that their conditions do not contradict one another. In that case, the form submission will fail.</p>\n\n\n\n<p>Once you finish customizing the Static Page Booking Form, update it and add it to the website page. We’ve created a dedicated <strong>All Workouts</strong> page, which shows a dynamic <a href=\"https://crocoblock.com/knowledge-base/articles/jetengine-how-to-create-a-listing-template-in-gutenberg/\" target=\"_blank\" rel=\"noreferrer noopener\">JetEngine Listing</a>. A booking form will look lovely under the grid.</p>\n\n\n\n<p>Open the page where you wish to place the form, find the <strong>JetForm </strong>block in the left sidebar, drag and drop it to the desired area, and then pick the form you’ve just edited in the <strong>Choose Form</strong> drop-down on the right sidebar.</p>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/14-jetform-addition-to-the-page.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/14-jetform-addition-to-the-page-1024x535.png\" alt=\"adding the appointment booking form to the Gutenberg page\" class=\"wp-image-623870\" width=\"768\" height=\"401\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/14-jetform-addition-to-the-page-1024x535.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/14-jetform-addition-to-the-page-300x157.png 300w, //oceanwp.org/wp-content/uploads/2021/11/14-jetform-addition-to-the-page-768x402.png 768w, //oceanwp.org/wp-content/uploads/2021/11/14-jetform-addition-to-the-page-1536x803.png 1536w, //oceanwp.org/wp-content/uploads/2021/11/14-jetform-addition-to-the-page.png 1572w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<p>Update the page and see how it looks on the front end.</p>\n\n\n\n<figure class=\"wp-block-video\"><video controls src=\"https://oceanwp.org/wp-content/uploads/2021/11/15-frontend-form-submission.m4v\"></video></figure>\n\n\n\n<p>Now, the most attentive readers are like, “How come the form is styled-up?”. That’s because we’ve thrown another plugin into the mix. Keep reading to find out which. <img src=\"https://s.w.org/images/core/emoji/13.1.0/72x72/1f642.png\" alt=\"🙂\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /></p>\n\n\n\n<p>Go back to the WordPress dashboard and open the <strong><em>Appointments &gt; Appointments</em></strong> pathway. There you have it – all appointments are sitting tight. You can view, edit, delete them, filter, and change their status here.</p>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><a href=\"https://oceanwp.org/wp-content/uploads/2021/11/16-gym-appointments.png\"><img src=\"https://oceanwp.org/wp-content/uploads/2021/11/16-gym-appointments-1024x522.png\" alt=\"all gym appointments in the list view\" class=\"wp-image-623869\" width=\"768\" height=\"392\" srcset=\"//oceanwp.org/wp-content/uploads/2021/11/16-gym-appointments-1024x522.png 1024w, //oceanwp.org/wp-content/uploads/2021/11/16-gym-appointments-300x153.png 300w, //oceanwp.org/wp-content/uploads/2021/11/16-gym-appointments-768x392.png 768w, //oceanwp.org/wp-content/uploads/2021/11/16-gym-appointments.png 1380w\" sizes=\"(max-width: 768px) 100vw, 768px\" /></a></figure>\n\n\n\n<p>As you can see, the appointment booking functionality is up and running. However, there are more things you can do to make the appointment scheduling website even more dynamic.</p>\n\n\n\n<p></p>\n<p style=\"margin: 0!important;padding: 26px 0 0!important;\"><a href=\"https://oceanwp.org/blog/how-to-create-a-dynamic-appointment-website-with-oceanwp-crocoblock/\" target=\"_blank\" style=\"display: inline-block; padding: 10px 30px; color: #ffffff; background-color: #13aff0; border-radius: 30px; font-size: 13px; font-weight: bold; line-height: 1.4; text-transform: uppercase; text-align: center; text-decoration: none; letter-spacing: 1px;\">Read More</a></p></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:36:\"http://wellformedweb.org/CommentAPI/\";a:1:{s:10:\"commentRss\";a:1:{i:0;a:5:{s:4:\"data\";s:98:\"https://oceanwp.org/blog/how-to-create-a-dynamic-appointment-website-with-oceanwp-crocoblock/feed/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:38:\"http://purl.org/rss/1.0/modules/slash/\";a:1:{s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:1:\"5\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:4;a:6:{s:4:\"data\";s:76:\"\n		\n		\n					\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n					\n			\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:5:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:62:\"Black Friday / Cyber Monday 2021: Best Deals for OceanWP Users\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:70:\"https://oceanwp.org/blog/black-friday-cyber-monday-special-deals-2021/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"comments\";a:1:{i:0;a:5:{s:4:\"data\";s:79:\"https://oceanwp.org/blog/black-friday-cyber-monday-special-deals-2021/#comments\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 